    string public constant VESTING_TVL_REACHED = "VESTING_TVL_REACHED";
    string public constant WRONG_TOKEN_ADDRESS = "WRONG_TOKEN_ADDRESS";
    string public constant GOVERNANCE_TOKEN_EXISTS = "GOVERNANCE_TOKEN_EXISTS";
    string public constant THRESHOLD_NOT_REACHED = "THRESHOLD_NOT_REACHED";
    string public constant UNSUPPORTED_TOKEN_TYPE = "UNSUPPORTED_TOKEN_TYPE";
    string public constant ALREADY_VOTED = "ALREADY_VOTED";
    string public constant ZERO_VOTES = "ZERO_VOTES";
    string public constant ACTIVE_GOVERNANCE_SETTINGS_PROPOSAL_EXISTS =
        "ACTIVE_GOVERNANCE_SETTINGS_PROPOSAL_EXISTS";
    string public constant EMPTY_ADDRESS = "EMPTY_ADDRESS";
    string public constant NOT_VALID_PROPOSER = "NOT_VALID_PROPOSER";
    string public constant SHARES_SUM_EXCEEDS_ONE = "SHARES_SUM_EXCEEDS_ONE";
    string public constant NOT_RESOLVER = "NOT_RESOLVER";
    string public constant NOT_REGISTRY = "NOT_REGISTRY";
    string public constant INVALID_TARGET = "INVALID_TARGET";
    string public constant NOT_TGE_FACTORY = "NOT_TGE_FACTORY";
    string public constant WRONG_AMOUNT = "WRONG_AMOUNT";
    string public constant WRONG_BLOCK_NUMBER = "WRONG_BLOCK_NUMBER";
    string public constant NOT_VALID_EXECUTOR = "NOT_VALID_EXECUTOR";
    string public constant POOL_PAUSED = "POOL_PAUSED";
    string public constant NOT_INVOICE_MANAGER = "NOT_INVOICE_MANAGER";
     string public constant WRONG_RESOLVER = "WRONG_RESOLVER";
}

contracts/Pool.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/structs/EnumerableSetUpgradeable.sol";
import "./governor/GovernorProposals.sol";
import "./interfaces/IService.sol";
import "./interfaces/IPool.sol";
import "./interfaces/IToken.sol";
import "./interfaces/ITGE.sol";
import "./interfaces/ICustomProposal.sol";
import "./interfaces/registry/IRecordsRegistry.sol";
import "./interfaces/registry/IRecordsRegistry.sol";
import "./libraries/ExceptionsLibrary.sol";

/// @dev These contracts are instances of on-chain implementations of user companies. The shareholders of the companies work with them, their addresses are used in the Registry contract as tags that allow obtaining additional legal information (before the purchase of the company by the client). They store legal data (after the purchase of the company by the client). Among other things, the contract is also the owner of the Token and TGE contracts.
/// @dev There can be an unlimited number of such contracts, including for one company owner. The contract can be in three states: 1) the company was created by the administrator, a record of it is stored in the Registry, but the contract has not yet been deployed and does not have an owner (buyer) 2) the contract is deployed, the company has an owner, but there is not yet a successful (softcap primary TGE), in this state its owner has the exclusive right to recreate the TGE in case of their failure (only one TGE can be launched at the same time) 3) the primary TGE ended successfully, softcap is assembled - the company has received the status of DAO.  The owner no longer has any exclusive rights, all the actions of the company are carried out through the creation and execution of propousals after voting. In this status, the contract is also a treasury - it stores the company's values in the form of ETH and/or ERC20 tokens.
contract Pool is
    Initializable,
    OwnableUpgradeable,
    ReentrancyGuardUpgradeable,
    PausableUpgradeable,
    GovernorProposals,
    IPool
{
    using EnumerableSetUpgradeable for EnumerableSetUpgradeable.AddressSet;
    using SafeERC20Upgradeable for IERC20Upgradeable;
    using AddressUpgradeable for address;
    using AddressUpgradeable for address payable;
    /// @dev The company's trade mark, label, brand name. It also acts as the Name of all the Governance tokens created for this pool.
    string public trademark;

    /// @dev When a buyer acquires a company, its record disappears from the Registry contract, but before that, the company's legal data is copied to this variable.
    ICompaniesRegistry.CompanyInfo public companyInfo;

    /// @dev Mapping for Governance Token. There can be only one valid Governance token.
    mapping(IToken.TokenType => address) public tokens;

    /// @dev last proposal id for address. This method returns the proposal Id for the last proposal created by the specified address.
    mapping(address => uint256) public lastProposalIdForAddress;

    /// @dev mapping for creation block
    mapping(uint256 => uint256) public proposalCreatedAt;

    /// @dev A list of tokens belonging to this pool. There can be only one valid Governance token and several Preference tokens with diffrent settings.
    mapping(IToken.TokenType => address[]) public tokensFullList;

    /// @dev token type by address
    mapping(address => IToken.TokenType) public tokenTypeByAddress;

    /// @dev pool secretary list
    EnumerableSetUpgradeable.AddressSet poolSecretary;

    /// @dev last executed proposal Id
    uint256 public lastExecutedProposalId;

    /// @dev mapping for proposalId to TGE address
    mapping(uint256 => address) public proposalIdToTGE;

    /// @dev pool executor list
    EnumerableSetUpgradeable.AddressSet poolExecutor;

    /// @dev Operating Agreement Url
    string public OAurl;

    // EVENTS

    // MODIFIER

    /// @dev Is executed when the main contract is applied. It is used to transfer control of Registry and deployable user contracts for the final configuration of the company.
    modifier onlyService() {
        require(msg.sender == address(service), ExceptionsLibrary.NOT_SERVICE);
        _;
    }

    modifier onlyTGEFactory() {
        require(
            msg.sender == address(service.tgeFactory()),
            ExceptionsLibrary.NOT_TGE_FACTORY
        );
        _;
    }

    modifier onlyServiceAdmin() {
        require(
            service.hasRole(service.ADMIN_ROLE(), msg.sender),
            ExceptionsLibrary.NOT_SERVICE_OWNER
        );
        _;
    }

    // INITIALIZER AND CONFIGURATOR

    /// @custom:oz-upgrades-unsafe-allow constructor

    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Initialization of a new pool and placement of user settings and data (including legal ones) in it
     * @param companyInfo_ Company info
     */
    function initialize(
        ICompaniesRegistry.CompanyInfo memory companyInfo_
    ) external initializer {
        __Ownable_init();
        __ReentrancyGuard_init();
        service = IService(msg.sender);
        companyInfo = companyInfo_;
    }

    /**
     * @dev set New Owner With Settings after company purchased
     * @param newowner New Pool owner
     * @param trademark_ trademark_
     * @param governanceSettings_ governance Settings
     */
    function setNewOwnerWithSettings(
        address newowner,
        string memory trademark_,
        NewGovernanceSettings memory governanceSettings_
    ) external onlyService {
        _transferOwnership(address(newowner));
        trademark = trademark_;
        _setGovernanceSettings(governanceSettings_);
    }

    /**
     * @dev set New Owner With Settings after company purchased
     * @param governanceSettings_ governance Settings
     * @param secretary secretary address list
     * @param executor executor address list
     */
    function setSettings(
        NewGovernanceSettings memory governanceSettings_,
        address[] memory secretary,
        address[] memory executor
    ) external {
        //only tgeFactory or pool
        require(
            msg.sender == address(service.tgeFactory()) ||
                msg.sender == address(this),
            ExceptionsLibrary.NOT_TGE_FACTORY
        );
        if (msg.sender == address(service.tgeFactory())) {
            if (address(getGovernanceToken()) != address(0)) {
                require(!isDAO(), ExceptionsLibrary.IS_DAO);
                require(
                    ITGE(getGovernanceToken().lastTGE()).state() !=
                        ITGE.State.Active,
                    ExceptionsLibrary.ACTIVE_TGE_EXISTS
                );
            }
        }
        _setGovernanceSettings(governanceSettings_);

        address[] memory values = poolSecretary.values();
        for (uint256 i = 0; i < values.length; i++) {
            poolSecretary.remove(values[i]);
        }

        for (uint256 i = 0; i < secretary.length; i++) {
            poolSecretary.add(secretary[i]);
        }

        values = poolExecutor.values();
        for (uint256 i = 0; i < values.length; i++) {
            poolExecutor.remove(values[i]);
        }

        for (uint256 i = 0; i < executor.length; i++) {
            poolExecutor.add(secretary[i]);
        }
    }

    /**
     * @dev sets new companyInfo and Operating Agreement Url
     * @param _jurisdiction Operating Agreement Url
     * @param _entityType Operating Agreement Url
     * @param _ein Operating Agreement Url
     * @param _dateOfIncorporation Operating Agreement Url
     * @param _OAuri Operating Agreement Url
     */
    function setCompanyInfo(
        uint256 _jurisdiction,
        uint256 _entityType,
        string memory _ein,
        string memory _dateOfIncorporation,
        string memory _OAuri
    ) external {
        require(
            msg.sender == address(service.registry()),
            ExceptionsLibrary.NOT_REGISTRY
        );
        companyInfo.jurisdiction = _jurisdiction;
        companyInfo.entityType = _entityType;
        companyInfo.ein = _ein;
        companyInfo.dateOfIncorporation = _dateOfIncorporation;
        OAurl = _OAuri;
    }

    // RECEIVE
    /// @dev Method for receiving an Ethereum contract that issues an event.
    receive() external payable {}

    // PUBLIC FUNCTIONS

    /**
     * @dev With this method, the owner of the Governance token of the pool can vote for one of the active propo-nodes, specifying its number and the value of the vote (for or against). One user can vote only once for one proposal with all the available balance that is in delegation at once.
     * @param proposalId Pool proposal ID
     * @param support Against or for
     */
    function castVote(
        uint256 proposalId,
        bool support
    ) external nonReentrant whenNotPaused {
        _castVote(proposalId, support);

        service.registry().log(
            msg.sender,
            address(this),
            0,
            abi.encodeWithSelector(IPool.castVote.selector, proposalId, support)
        );
    }

    // RESTRICTED PUBLIC FUNCTIONS

    /**
     * @dev Adding a new entry about the deployed token contract to the list of tokens related to the pool.
     * @param token_ Token address
     * @param tokenType_ Token type
     */
    function setToken(
        address token_,
        IToken.TokenType tokenType_
    ) external onlyTGEFactory {
        require(token_ != address(0), ExceptionsLibrary.ADDRESS_ZERO);
        if (tokenExists(IToken(token_))) return;
        if (tokenType_ == IToken.TokenType.Governance) {
            // Check that there is no governance tokens or tge failed
            require(
                address(getGovernanceToken()) == address(0) ||
                    ITGE(getGovernanceToken().getTGEList()[0]).state() ==
                    ITGE.State.Failed,
                ExceptionsLibrary.GOVERNANCE_TOKEN_EXISTS
            );
            tokens[IToken.TokenType.Governance] = token_;
            if (tokensFullList[tokenType_].length > 0) {
                tokensFullList[tokenType_].pop();
            }
        }
        tokensFullList[tokenType_].push(token_);
        tokenTypeByAddress[address(token_)] = tokenType_;
    }

    /**
     * @dev set value to mapping Proposal IdTo TGE
     * @param tge tge address
     */
    function setProposalIdToTGE(address tge) external onlyTGEFactory {
        proposalIdToTGE[lastExecutedProposalId] = tge;
    }

    /**
     * @dev Execute proposal
     * @param proposalId Proposal ID
     */
    function executeProposal(uint256 proposalId) external whenNotPaused {
        require(
            isValidExecutor(msg.sender),
            ExceptionsLibrary.NOT_VALID_EXECUTOR
        );

        lastExecutedProposalId = proposalId;
        _executeProposal(proposalId, service);

        service.registry().log(
            msg.sender,
            address(this),
            0,
            abi.encodeWithSelector(IPool.executeProposal.selector, proposalId)
        );
    }

    /**
     * @dev Cancel proposal, callable only by Service
     * @param proposalId Proposal ID
     */
    function cancelProposal(uint256 proposalId) external onlyService {
        _cancelProposal(proposalId);
    }

    /**
     * @dev Creating a proposal and assigning it a unique identifier to store in the list of proposals in the Governor contract.
     * @param core Proposal core data
     * @param meta Proposal meta data
     */
    function propose(
        address proposer,
        uint256 proposeType,
        IGovernor.ProposalCoreData memory core,
        IGovernor.ProposalMetaData memory meta
    ) external returns (uint256 proposalId) {
        require(
            msg.sender == address(service.customProposal()) &&
                isValidProposer(proposer),
            ExceptionsLibrary.NOT_VALID_PROPOSER
        );

        core.quorumThreshold = quorumThreshold;
        core.decisionThreshold = decisionThreshold;
        core.executionDelay = executionDelays[meta.proposalType];
        uint256 proposalId_ = _propose(
            core,
            meta,
            votingDuration,
            votingStartDelay
        );
        lastProposalIdByType[proposeType] = proposalId_;

        _setLastProposalIdForAddress(proposer, proposalId_);

        service.registry().log(
            msg.sender,
            address(this),
            0,
            abi.encodeWithSelector(
                IPool.propose.selector,
                proposer,
                proposeType,
                core,
                meta
            )
        );

        return proposalId_;
    }

    /**
     * @dev Transfers funds from treasury if the pool is not yeta  DAO
     * @param to receiver addresss
     * @param amount transfer amount
     * @param unitOfAccount unitOfAccount (token contract address or address(0) for eth)
     */
    function transferByOwner(
        address to,
        uint256 amount,
        address unitOfAccount
    ) external onlyOwner {
        //only if pool is yet DAO
        require(!isDAO(), ExceptionsLibrary.IS_DAO);

        if (unitOfAccount == address(0)) {
            require(
                address(this).balance >= amount,
                ExceptionsLibrary.WRONG_AMOUNT
            );

            (bool success, ) = payable(to).call{value: amount}("");
            require(success, ExceptionsLibrary.WRONG_AMOUNT);
        } else {
            require(
                IERC20Upgradeable(unitOfAccount).balanceOf(address(this)) >=
                    amount,
                ExceptionsLibrary.WRONG_AMOUNT
            );

            IERC20Upgradeable(unitOfAccount).safeTransferFrom(
                msg.sender,
                to,
                amount
            );
        }
    }

    /**
     * @dev Execute custom tx if the pool is not yet a  DAO
     * @param target receiver addresss
     * @param value transfer amount
     * @param data input data of the transaction
     */
    function customTxByOwner(
        address target,
        uint256 value,
        bytes calldata data
    ) external onlyOwner {
        //only if pool is yet DAO
        require(!isDAO(), ExceptionsLibrary.IS_DAO);

        target.functionCallWithValue(data, value);
    }

    // PUBLIC VIEW FUNCTIONS

    /**
     * @dev Getter showing whether this company has received the status of a DAO as a result of the successful completion of the primary TGE (that is, launched by the owner of the company and with the creation of a new Governance token). After receiving the true status, it is not transferred back. This getter is responsible for the basic logic of starting a contract as managed by token holders.
     * @return Is any governance TGE successful
     */
    function isDAO() public view returns (bool) {
        if (address(getGovernanceToken()) == address(0)) {
            return false;
        } else {
            return getGovernanceToken().isPrimaryTGESuccessful();
        }
    }

    /**
     * @dev Return pool owner
     * @return Owner address
     */
    function owner()
        public
        view
        override(IPool, OwnableUpgradeable)
        returns (address)
    {
        return super.owner();
    }

    /// @dev This getter is needed in order to return a Token contract addresses depending on the type of token requested (Governance or Preference).
    function getTokens(
        IToken.TokenType tokenType
    ) external view returns (address[] memory) {
        return tokensFullList[tokenType];
    }

    /// @dev This getter returns current Governance Token For this Pool
    function getGovernanceToken() public view returns (IToken) {
        return IToken(tokens[IToken.TokenType.Governance]);
    }

    /**
     * @dev Getter checks token exists for this pool
     * @return bool
     */
    function tokenExists(IToken token) public view returns (bool) {
        return
            tokenTypeByAddress[address(token)] == IToken.TokenType.None
                ? false
                : true;
    }

    /// @dev This getter to show list of current pool Secretary
    function getPoolSecretary() external view returns (address[] memory) {
        return poolSecretary.values();
    }

    /// @dev This getter to show list of current pool executor
    function getPoolExecutor() external view returns (address[] memory) {
        return poolExecutor.values();
    }

    /// @dev This getter shows if address is pool Secretary
    function isPoolSecretary(address account) public view returns (bool) {
        return poolSecretary.contains(account);
    }

    /// @dev This getter shows if address is pool executor
    function isPoolExecutor(address account) public view returns (bool) {
        return poolExecutor.contains(account);
    }

    /// @dev This getter check if address can propose
    function isValidProposer(address account) public view returns (bool) {
        if (
            _getCurrentVotes(account) >= proposalThreshold ||
            isPoolSecretary(account) ||
            service.hasRole(service.SERVICE_MANAGER_ROLE(), msg.sender)
        ) return true;

        return false;
    }

    /// @dev This getter check if address can execute ballot
    function isValidExecutor(address account) public view returns (bool) {
        if (
            poolExecutor.length() == 0 ||
            isPoolExecutor(account) ||
            service.hasRole(service.SERVICE_MANAGER_ROLE(), account)
        ) return true;

        return false;
    }

    /// @dev This getter returns if Last Proposal By Type is Active
    function isLastProposalIdByTypeActive(
        uint256 type_
    ) public view returns (bool) {
        if (proposalState(lastProposalIdByType[type_]) == ProposalState.Active)
            return true;

        return false;
    }

    /// @dev This getter validate Governance Settings
    function validateGovernanceSettings(
        NewGovernanceSettings memory settings
    ) external pure {
        _validateGovernanceSettings(settings);
    }

    /// @dev This getter returns if available Votes For Proposal by Id
    function availableVotesForProposal(
        uint256 proposalId
    ) external view returns (uint256) {
        if (proposals[proposalId].vote.startBlock - 1 < block.number)
            return
                _getBlockTotalVotes(proposals[proposalId].vote.startBlock - 1);
        else return _getBlockTotalVotes(block.number - 1);
    }

    // INTERNAL FUNCTIONS

    function _afterProposalCreated(uint256 proposalId) internal override {
        service.addProposal(proposalId);
    }

    /**
     * @dev Function that gets amount of votes for given account
     * @param account Account's address
     * @return Amount of votes
     */
    function _getCurrentVotes(address account) internal view returns (uint256) {
        return getGovernanceToken().getVotes(account);
    }

    /**
     * @dev Function that returns the total amount of votes in the pool in block
     * @param blocknumber blocknumber
     * @return Amount of votes
     */
    function _getBlockTotalVotes(
        uint256 blocknumber
    ) internal view override returns (uint256) {
        return
            IToken(tokens[IToken.TokenType.Governance]).getPastTotalSupply(
                blocknumber
            );
    }

    /**
     * @dev Function that gets amount of votes for given account at given block
     * @param account Account's address
     * @param blockNumber Block number
     * @return Account's votes at given block
     */
    function _getPastVotes(
        address account,
        uint256 blockNumber
    ) internal view override returns (uint256) {
        return getGovernanceToken().getPastVotes(account, blockNumber);
    }

    /**
     * @dev This function stores the proposal id for the last proposal created by the proposer address.
     * @param proposer Proposer's address
     * @param proposalId Proposal id
     */
    function _setLastProposalIdForAddress(
        address proposer,
        uint256 proposalId
    ) internal override {
        lastProposalIdForAddress[proposer] = proposalId;
    }
}

contracts/Registry.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

import "./registry/CompaniesRegistry.sol";
import "./registry/RecordsRegistry.sol";
import "./registry/TokensRegistry.sol";

/// @dev The repository of all user and business entities created by the protocol: companies to be implemented, contracts to be deployed, proposal created by shareholders.
contract Registry is CompaniesRegistry, RecordsRegistry, TokensRegistry {
    /// @dev Mapping of pool contracts and local proposal ids to their global ids
    mapping(address => mapping(uint256 => uint256)) public globalProposalIds;
    event Log(address sender, address receiver, uint256 value, bytes data);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Initializer
     */
    function initialize() public initializer {
        __RegistryBase_init();
    }

    // INTERNAL FUNCTIONS

    /**
     * @dev Update global proposal ID
     * @param pool Pool address
     * @param proposalId Local Proposal ID
     * @param globalProposalId Global Proposal ID
     */
    function setGlobalProposalId(
        address pool,
        uint256 proposalId,
        uint256 globalProposalId
    ) internal override {
        globalProposalIds[pool][proposalId] = globalProposalId;
    }

    // VIEW FUNCTIONS

    /**
     * @dev Return global proposal ID
     * @param pool Pool address
     * @param proposalId Proposal ID
     * @return Global proposal ID
     */
    function getGlobalProposalId(
        address pool,
        uint256 proposalId
    ) public view returns (uint256) {
        return globalProposalIds[pool][proposalId];
    }

    function log(
        address sender,
        address receiver,
        uint256 value,
        bytes memory data
    ) external {
        require(
            msg.sender == address(this) ||
                msg.sender == service ||
                msg.sender == address(IService(service).tgeFactory()) ||
                msg.sender == address(IService(service).invoice()) ||
                typeOf(msg.sender) == IRecordsRegistry.ContractType.Pool ||
                typeOf(msg.sender) == IRecordsRegistry.ContractType.TGE ||
                typeOf(msg.sender) ==
                IRecordsRegistry.ContractType.GovernanceToken ||
                typeOf(msg.sender) == IRecordsRegistry.ContractType.PreferenceToken,
            ExceptionsLibrary.INVALID_USER
        );
        // Emit event
        emit Log(sender, receiver, value, data);
    }
}

contracts/registry/CompaniesRegistry.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

import "./RegistryBase.sol";
import "../libraries/ExceptionsLibrary.sol";
import "../interfaces/IService.sol";
import "../interfaces/IPool.sol";
import "../interfaces/registry/ICompaniesRegistry.sol";

abstract contract CompaniesRegistry is RegistryBase, ICompaniesRegistry {
    // CONSTANTS

    /// @dev The constant determines by which role ID contains a list of wallets that have been assigned as managers who have the ability to create new company records in the Registry company repository.
    bytes32 public constant COMPANIES_MANAGER_ROLE =
        keccak256("COMPANIES_MANAGER");

    // STORAGE

    /// @dev The embedded mappings form a construction, when accessed using two keys at once [jurisdiction][EntityType], you can get lists of ordinal numbers of company records added by managers. These serial numbers can be used when contacting mapping companies to obtain public legal information about the company awaiting purchase by the client.
    mapping(uint256 => mapping(uint256 => uint256[])) public queue;

    /// @dev In this mapping, public legal information is stored about companies that are ready to be acquired by the client and start working as a DAO. The appeal takes place according to the serial number - the key. A list of keys for each type of company and each jurisdiction can be obtained in the queue mapping.
    mapping(uint256 => CompanyInfo) public companies;

    /// @dev The last sequential number of the last record created by managers in the queue with company data is stored here.
    uint256 public lastCompanyIndex;

    /// @dev Status of combination of (jurisdiction, entityType, EIN) existing
    mapping(bytes32 => uint256) public companyIndex;

    // EVENTS

    /**
     * @dev Event emitted on company creation
     * @param index Company list index
     * @param poolAddress Future pool address
     */
    event CompanyCreated(uint256 index, address poolAddress);

    /**
     * @dev Event emitted on company creation.
     * @param metadataIndex Company metadata index
     */
    event CompanyDeleted(uint256 metadataIndex);

    /**
     * @dev The event is issued when the manager changes the price of an already created company ready for purchase by the client.
     * @param jurisdiction Jurisdiction
     * @param entityType Entity type
     * @param id Queue index
     * @param fee Fee for createPool
     */
    event CompanyFeeUpdated(
        uint256 jurisdiction,
        uint256 entityType,
        uint256 id,
        uint256 fee
    );

    // PUBLIC FUNCTIONS

    /**
     * @dev Create company record - A method for creating a new company record, including its legal data and the sale price.
     * @param info Company Info
     */
    function createCompany(
        CompanyInfo calldata info
    ) public onlyRole(COMPANIES_MANAGER_ROLE) {
        // Check that company data is valid
        require(
            info.jurisdiction > 0 &&
                bytes(info.ein).length != 0 &&
                bytes(info.dateOfIncorporation).length != 0 &&
                info.entityType > 0,
            ExceptionsLibrary.VALUE_ZERO
        );

        // Check that such company does not exist yet and mark it as existing
        bytes32 companyHash = keccak256(
            abi.encodePacked(info.jurisdiction, info.entityType, info.ein)
        );
        require(companyIndex[companyHash] == 0, ExceptionsLibrary.INVALID_EIN);
        uint256 index = ++lastCompanyIndex;
        companyIndex[companyHash] = index;

        // Add record to list
        companies[index] = info;

        // Add record to queue
        queue[info.jurisdiction][info.entityType].push(index);

        // Emit event
        emit CompanyCreated(index, IService(service).getPoolAddress(info));

        //Create PoolContract
        IService(service).createPool(info);

        IRegistry(address(this)).log(
            msg.sender,
            address(this),
            0,
            abi.encodeWithSelector(ICompaniesRegistry.createCompany.selector, info)
        );
    }

    /**
     * @dev Lock company record - Booking the company for the buyer. During the acquisition of a company, this method searches for a free company at the request of the client (jurisdiction and type of organization), if such exist in the companys storage reserve, then the method selects the last of the added companies, extracts its record data and sends it as a response for further work of the Service contract, removes its record from the Registry.
     * @return info Company info
     */
    function lockCompany(
        uint256 jurisdiction,
        uint256 entityType
    ) external onlyService returns (CompanyInfo memory info) {
        // Check that company is available
        uint256 queueLength = queue[jurisdiction][entityType].length;
        require(queueLength > 0, ExceptionsLibrary.NO_COMPANY);

        // Get index and pop queue
        uint256 index = queue[jurisdiction][entityType][queueLength - 1];
        queue[jurisdiction][entityType].pop();

        // Get company info and remove it from list
        info = companies[index];
        delete companies[index];
    }

    /**
     * @dev Delete queue record
     * @param jurisdiction Jurisdiction
     * @param entityType Entity type
     * @param id Queue index
     */
    function deleteCompany(
        uint256 jurisdiction,
        uint256 entityType,
        uint256 id
    ) external onlyRole(COMPANIES_MANAGER_ROLE) {
        // Get index and pop queue
        uint256 index = queue[jurisdiction][entityType][id];
        uint256 lastId = queue[jurisdiction][entityType].length - 1;
        queue[jurisdiction][entityType][id] = queue[jurisdiction][entityType][
            lastId
        ];
        queue[jurisdiction][entityType].pop();

        // Remove company from list
        string memory ein = companies[index].ein;
        delete companies[index];

        // Mark company as not existing
        bytes32 companyHash = keccak256(
            abi.encodePacked(jurisdiction, entityType, ein)
        );
        companyIndex[companyHash] = 0;

        // Emit event
        emit CompanyDeleted(id);
    }

    /**
     * @dev The method that the manager uses to change the value of the company already added earlier in the Registry.
     * @param jurisdiction Jurisdiction
     * @param entityType Entity type
     * @param id Queue index
     * @param fee Fee to update
     */
    function updateCompanyFee(
        uint256 jurisdiction,
        uint256 entityType,
        uint256 id,
        uint256 fee
    ) external onlyRole(COMPANIES_MANAGER_ROLE) {
        // Update fee
        uint256 index = queue[jurisdiction][entityType][id];
        companies[index].fee = fee;

        // Emit event
        emit CompanyFeeUpdated(jurisdiction, entityType, id, fee);
    }

    // PUBLIC VIEW FUNCTIONS

    /**
     * @dev This view method is designed to find out whether there is at least one company available for purchase for the jurisdiction and type of organization selected by the user.
     * @param jurisdiction Jurisdiction
     * @param entityType Entity type
     * @return Flag if company is available
     */
    function companyAvailable(
        uint256 jurisdiction,
        uint256 entityType
    ) external view returns (bool) {
        return queue[jurisdiction][entityType].length > 0;
    }

    /**
     * @dev Get company pool address by metadata
     * @param jurisdiction Jurisdiction
     * @param entityType Entity type
     * @param id Queue id
     * @return Future company's pool address
     */
    function getCompanyPoolAddress(
        uint256 jurisdiction,
        uint256 entityType,
        uint256 id
    ) public view returns (address) {
        uint256 index = queue[jurisdiction][entityType][id];
        return IService(service).getPoolAddress(companies[index]);
    }

    /**
     * @dev Get company array by metadata
     * @param jurisdiction Jurisdiction
     * @param entityType Entity type
     * @param ein EIN
     * @return Company data
     */
    function getCompany(
        uint256 jurisdiction,
        uint256 entityType,
        string calldata ein
    ) external view returns (CompanyInfo memory) {
        bytes32 companyHash = keccak256(
            abi.encodePacked(jurisdiction, entityType, ein)
        );
        return companies[companyIndex[companyHash]];
    }

    /**
     * @dev This view method is designed to show available for purchase for the jurisdiction and type of organization selected by the user.
     * @param jurisdiction Jurisdiction
     * @param entityType Entity type
     * @return CompanyInfo
     */
    function getAvailableCompanyAddress(
        uint256 jurisdiction,
        uint256 entityType
    ) external view returns (address) {
        // Check that company is available
        uint256 queueLength = queue[jurisdiction][entityType].length;
        require(queueLength > 0, ExceptionsLibrary.NO_COMPANY);

        // Get index
        uint256 index = queue[jurisdiction][entityType][queueLength - 1];

        address companyAddress = IService(service).getPoolAddress(
            companies[index]
        );

        return companyAddress;
    }

    /**
     * @dev set Operating Agreement Url for pool
     * @param pool pool adress
     * @param _jurisdiction Operating Agreement Url
     * @param _entityType Operating Agreement Url
     * @param _ein Operating Agreement Url
     * @param _dateOfIncorporation Operating Agreement Url
     * @param _OAuri Operating Agreement Url
     */
    function setCompanyInfoForPool(
        IPool pool,
        uint256 _jurisdiction,
        uint256 _entityType,
        string memory _ein,
        string memory _dateOfIncorporation,
        string memory _OAuri
    ) external onlyRole(COMPANIES_MANAGER_ROLE)  {
        pool.setCompanyInfo(
            _jurisdiction,
            _entityType,
            _ein,
            _dateOfIncorporation,
            _OAuri
        );
    }
}

contracts/registry/RecordsRegistry.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

import "./RegistryBase.sol";
import "../interfaces/registry/IRecordsRegistry.sol";

abstract contract RecordsRegistry is RegistryBase, IRecordsRegistry {
    // STORAGE

    /// @dev In this array, records are stored about all contracts created by users (that is, about those generated by the service), namely, its index, with which you can extract all available information from other getters.
    ContractInfo[] public contractRecords;

    struct ContractIndex {
        bool exists;
        uint160 index;
    }

    /// @dev Mapping of contract addresses to their record indexes
    mapping(address => ContractIndex) public indexOfContract;

    /// @dev List of proposal records
    ProposalInfo[] public proposalRecords;

    /// @dev A list of existing events. An event can be either a contract or a specific action performed by a pool based on the results of voting for a promotion (for example, the transfer of funds from a pool contract is considered an event, but does not have a contract, and TGE has both the status of an event and its own separate contract).
    Event[] public events;

    // EVENTS

    /**
     * @dev Event emitted on creation of contract record
     * @param index Record index
     * @param addr Contract address
     * @param contractType Contract type
     */
    event ContractRecordAdded(
        uint256 index,
        address addr,
        ContractType contractType
    );

    /**
     * @dev Event emitted on creation of proposal record
     * @param index Record index
     * @param pool Pool address
     * @param proposalId Proposal ID
     */
    event ProposalRecordAdded(uint256 index, address pool, uint256 proposalId);

    /**
     * @dev Event emitted on creation of event
     * @param index Record index
     * @param eventType Event type
     * @param pool Pool address
     * @param proposalId Proposal ID
     */
    event EventRecordAdded(
        uint256 index,
        EventType eventType,
        address pool,
        uint256 proposalId
    );



    // PUBLIC FUNCTIONS

    /**
     * @dev This method is used by the main Service contract in order to save the data of the contracts it deploys. After the Registry contract receives the address and type of the created contract from the Service contract, it sends back as a response the sequence number/index assigned to the new record.
     * @param addr Contract address
     * @param contractType Contract type
     * @return index Record index
     */
    function addContractRecord(
        address addr,
        ContractType contractType,
        string memory description
    ) external onlyServiceOrFactory returns (uint256 index) {
        // Add record
        contractRecords.push(
            ContractInfo({
                addr: addr,
                contractType: contractType,
                description: description
            })
        );
        index = contractRecords.length - 1;

        // Add mapping from contract address
        indexOfContract[addr] = ContractIndex({
            exists: true,
            index: uint160(index)
        });

        // Emit event
        emit ContractRecordAdded(index, addr, contractType);
    }

    /**
     * @dev This method accepts data from the Service contract about the created nodes in the pools. If there is an internal index of the proposal in the contract of the pool whose shareholders created the proposal, then as a result of using this method, the proposal is given a global index for the entire ecosystem.
     * @param pool Pool address
     * @param proposalId Proposal ID
     * @return index Record index
     */
    function addProposalRecord(
        address pool,
        uint256 proposalId
    ) external onlyService returns (uint256 index) {
        // Add record
        proposalRecords.push(
            ProposalInfo({pool: pool, proposalId: proposalId, description: ""})
        );
        index = proposalRecords.length - 1;
        setGlobalProposalId(pool, proposalId, index);
        // Emit event
        emit ProposalRecordAdded(index, pool, proposalId);
    }

    /**
     * @dev This method is used to register events - specific entities associated with the operational activities of pools and the transfer of various values as a result of the use of ecosystem contracts. Each event also has a metahash string field, which is the identifier of the private description of the event stored on the backend.
     * @param pool Pool address
     * @param eventType Event type
     * @param eventContract Address of the event contract
     * @param proposalId Proposal ID
     * @param metaHash Hash value of event metadata
     * @return index Record index
     */
    function addEventRecord(
        address pool,
        EventType eventType,
        address eventContract,
        uint256 proposalId,
        string calldata metaHash
    ) external onlyServiceOrFactory returns (uint256 index) {
        // Add record
        events.push(
            Event({
                eventType: eventType,
                pool: pool,
                eventContract: eventContract,
                proposalId: proposalId,
                metaHash: metaHash
            })
        );
        index = events.length - 1;

        // Emit event
        emit EventRecordAdded(index, eventType, pool, proposalId);
    }



    // VIRTUAL FUNCTIONS

    function setGlobalProposalId(
        address pool,
        uint256 proposalId,
        uint256 globalProposalId
    ) internal virtual;

    // PUBLIC VIEW FUNCTIONS

    /**
     * @notice Returns type of given contract
     * @param addr Address of contract
     * @return Contract type
     */
    function typeOf(address addr) public view returns (ContractType) {
        ContractIndex memory index = indexOfContract[addr];
        return
            index.exists
                ? contractRecords[index.index].contractType
                : ContractType.None;
    }

    /**
     * @notice Returns number of contract records
     * @return Contract records count
     */
    function contractRecordsCount() external view returns (uint256) {
        return contractRecords.length;
    }

    /**
     * @notice Returns number of proposal records
     * @return Proposal records count
     */
    function proposalRecordsCount() external view returns (uint256) {
        return proposalRecords.length;
    }

    /**
     * @notice Returns number of event records
     * @return Event records count
     */
    function eventRecordsCount() external view returns (uint256) {
        return events.length;
    }
}

contracts/registry/RegistryBase.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

import "@openzeppelin/contracts-upgradeable/access/AccessControlEnumerableUpgradeable.sol";
import "../libraries/ExceptionsLibrary.sol";
import "../interfaces/IService.sol";

abstract contract RegistryBase is AccessControlEnumerableUpgradeable {
    // STORAGE

    /// @dev Service address
    address public service;

    // MODIFIERS

    modifier onlyService() {
        require(msg.sender == service, ExceptionsLibrary.NOT_SERVICE);
        _;
    }

    modifier onlyServiceOrFactory() {
        bool isService = msg.sender == service;
        bool isFactory = msg.sender ==
            address(IService(service).tokenFactory()) ||
            msg.sender == address(IService(service).tgeFactory());

        require(isService || isFactory, ExceptionsLibrary.NOT_SERVICE);
        _;
    }

    // INITIALIZER

    function __RegistryBase_init() internal onlyInitializing {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    // PUBLIC FUNCTIONS

    function setService(
        address service_
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        service = service_;
    }
}

contracts/registry/TokensRegistry.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

import "./RegistryBase.sol";
import "../libraries/ExceptionsLibrary.sol";
import "../interfaces/registry/ITokensRegistry.sol";

abstract contract TokensRegistry is RegistryBase, ITokensRegistry {
    // CONSTANTS

    /// @dev Whitelisted token role
    bytes32 public constant WHITELISTED_TOKEN_ROLE =
        keccak256("WHITELISTED_TOKEN");

    // PUBLIC FUNCTIONS

    function whitelistTokens(
        address[] calldata tokens
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        for (uint256 i = 0; i < tokens.length; i++) {
            _grantRole(WHITELISTED_TOKEN_ROLE, tokens[i]);
        }
    }

    // PUBLIC VIEW FUNCTIONS

    /**
     * @dev Check if token is whitelisted
     * @param token Token
     * @return Is token whitelisted
     */
    function isTokenWhitelisted(address token) external view returns (bool) {
        return hasRole(WHITELISTED_TOKEN_ROLE, token);
    }
}

contracts/Service.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

import "@openzeppelin/contracts-upgradeable/access/AccessControlEnumerableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/utils/structs/EnumerableSetUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol";
import "@openzeppelin/contracts/proxy/beacon/BeaconProxy.sol";
import "@openzeppelin/contracts-upgradeable/utils/Create2Upgradeable.sol";
import "./interfaces/IService.sol";
import "./interfaces/IPool.sol";
import "./interfaces/IToken.sol";
import "./interfaces/ITGE.sol";
import "./interfaces/IVesting.sol";
import "./interfaces/IInvoice.sol";
import "./interfaces/registry/IRegistry.sol";
import "./interfaces/ICustomProposal.sol";
import "./interfaces/registry/IRecordsRegistry.sol";
import "./libraries/ExceptionsLibrary.sol";

/// @dev The main service contract through which the administrator manages the project, assigns roles to individual wallets, changes service commissions, and also through which the user creates pool contracts. Exists in a single copy.
contract Service is
    Initializable,
    AccessControlEnumerableUpgradeable,
    ReentrancyGuardUpgradeable,
    PausableUpgradeable,
    IService
{
    using EnumerableSetUpgradeable for EnumerableSetUpgradeable.AddressSet;
    using AddressUpgradeable for address;

    // CONSTANTS

    /// @notice Denominator for shares (such as thresholds)
    uint256 private constant DENOM = 100 * 10 ** 4;

    /// @notice Default admin  role
    bytes32 public constant ADMIN_ROLE = DEFAULT_ADMIN_ROLE;

    /// @notice User manager role
    bytes32 public constant SERVICE_MANAGER_ROLE = keccak256("USER_MANAGER");

    /// @notice User role
    bytes32 public constant WHITELISTED_USER_ROLE =
        keccak256("WHITELISTED_USER");

    /// @notice Executor role
    bytes32 public constant EXECUTOR_ROLE = keccak256("EXECUTOR");

    // STORAGE

    /// @dev Registry contract
    IRegistry public registry;

    /// @dev Pool beacon
    address public poolBeacon;

    /// @dev Token beacon
    address public tokenBeacon;

    /// @dev TGE beacon
    address public tgeBeacon;

    /// @dev There gets 0.1% (the value can be changed by the admin) of all Governance tokens from successful TGE
    address public protocolTreasury;

    /// @dev protocol token fee percentage value with 4 decimals. Examples: 1% = 10000, 100% = 1000000, 0.1% = 1000
    uint256 public protocolTokenFee;

    /// @dev protocol token fee claimed for tokens
    mapping(address => uint256) public protolCollectedFee;

    /// @dev Proposal beacon
    ICustomProposal public customProposal;

    /// @dev Vesting contract
    IVesting public vesting;

    /// @dev TokenFactory contract
    IInvoice public invoice;

    /// @dev TokenFactory contract
    ITokenFactory public tokenFactory;

    /// @dev TGEFactory contract
    ITGEFactory public tgeFactory;

    /// @dev Token beacon
    address public tokenERC1155Beacon;

    // EVENTS

    /**
     * @dev Event emitted on pool creation.
     * @param pool Pool address
     * @param token Pool token address
     * @param tge Pool primary TGE address
     */
    event PoolCreated(address pool, address token, address tge);

    /**
     * @dev Event that emits when a pool is purchased.
     * @param pool Pool address
     * @param token Pool token address
     * @param tge Pool primary TGE address
     */
    event PoolPurchased(address pool, address token, address tge);

    /**
     * @dev Event emitted on protocol treasury change.
     * @param protocolTreasury Protocol treasury address
     */
    event ProtocolTreasuryChanged(address protocolTreasury);

    /**
     * @dev Event emitted on protocol token fee change.
     * @param protocolTokenFee Protocol token fee
     */
    event ProtocolTokenFeeChanged(uint256 protocolTokenFee);

    /**
     * @dev Event emitted on transferring collected fees.
     * @param to Transfer recepient
     * @param amount Amount of transferred ETH
     */
    event FeesTransferred(address to, uint256 amount);

    /**
     * @dev Event emitted on proposal cacellation by service owner.
     * @param pool Pool address
     * @param proposalId Pool local proposal id
     */
    event ProposalCancelled(address pool, uint256 proposalId);

    /**
     * @dev Event emitted on PoolBeacon change.
     * @param beacon Beacon address
     */
    event PoolBeaconChanged(address beacon);

    /**
     * @dev Event emitted on TGEBeacon change.
     * @param beacon Beacon address
     */
    event TGEBeaconChanged(address beacon);
    /**
     * @dev Event emitted on TokenBeacon change.
     * @param beacon Beacon address
     */
    event TokenBeaconChanged(address beacon);
    /**
     * @dev Event emitted on CustomPropsalProxy change.
     * @param proxy Proxy address
     */
    event CustomPropsalChanged(address proxy);
    /**
     * @dev Event emitted on InvoiceProxy change.
     * @param proxy Proxy address
     */
    event InvoiceChanged(address proxy);
    /**
     * @dev Event emitted on RegistryProxy change.
     * @param proxy Proxy address
     */
    event RegistryChanged(address proxy);
    /**
     * @dev Event emitted on TGEFactoryProxy change.
     * @param proxy Proxy address
     */
    event TGEFactoryChanged(address proxy);

    /**
     * @dev Event emitted on TokenFactoryProxy change.
     * @param proxy Proxy address
     */
    event TokenFactoryChanged(address proxy);

    /**
     * @dev Event emitted on VestingProxy change.
     * @param proxy Proxy address
     */
    event VestingChanged(address proxy);
    // MODIFIERS

    modifier onlyPool() {
        require(
            registry.typeOf(msg.sender) == IRecordsRegistry.ContractType.Pool,
            ExceptionsLibrary.NOT_POOL
        );
        _;
    }

    modifier onlyTGE() {
        require(
            registry.typeOf(msg.sender) == IRecordsRegistry.ContractType.TGE,
            ExceptionsLibrary.NOT_TGE
        );
        _;
    }

    modifier onlyRegistry() {
        require(
            msg.sender == address(registry),
            ExceptionsLibrary.NOT_REGISTRY
        );
        _;
    }

    modifier onlyManager() {
        require(
            registry.hasRole(registry.COMPANIES_MANAGER_ROLE(), msg.sender),
            ExceptionsLibrary.INVALID_USER
        );
        _;
    }

    // INITIALIZER AND CONSTRUCTOR

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Initializer function, can only be called once
     * @param registry_ Registry address
     * @param customProposal_ Custom proposals address
     * @param vesting_ Vesting address
     * @param poolBeacon_ Pool beacon
     * @param tokenBeacon_ Governance token beacon
     * @param tgeBeacon_ TGE beacon
     * @param protocolTokenFee_ Protocol token fee
     */
    function initialize(
        IRegistry registry_,
        ICustomProposal customProposal_,
        IVesting vesting_,
        address poolBeacon_,
        address tokenBeacon_,
        address tgeBeacon_,
        uint256 protocolTokenFee_
    ) external reinitializer(2) {
        require(
            address(registry_) != address(0),
            ExceptionsLibrary.ADDRESS_ZERO
        );
        require(poolBeacon_ != address(0), ExceptionsLibrary.ADDRESS_ZERO);
        require(tokenBeacon_ != address(0), ExceptionsLibrary.ADDRESS_ZERO);
        require(tgeBeacon_ != address(0), ExceptionsLibrary.ADDRESS_ZERO);
        __Pausable_init();
        __ReentrancyGuard_init();

        registry = registry_;
        vesting = vesting_;
        poolBeacon = poolBeacon_;
        tokenBeacon = tokenBeacon_;
        tgeBeacon = tgeBeacon_;
        customProposal = customProposal_;
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(SERVICE_MANAGER_ROLE, msg.sender);
        _grantRole(EXECUTOR_ROLE, msg.sender);
        _setRoleAdmin(WHITELISTED_USER_ROLE, SERVICE_MANAGER_ROLE);

        setProtocolTreasury(address(this));
        setProtocolTokenFee(protocolTokenFee_);
    }

    // PUBLIC FUNCTIONS

    /**
     * @dev Method for purchasing a pool by the user. Among the data submitted for input, there are jurisdiction and Entity Type
     * @param jurisdiction jurisdiction
     * @param entityType entityType
     */
    function purchasePool(
        uint256 jurisdiction,
        uint256 entityType,
        string memory trademark,
        IGovernanceSettings.NewGovernanceSettings memory governanceSettings
    ) external payable nonReentrant whenNotPaused {
        // Lock company
        IRegistry.CompanyInfo memory companyInfo = registry.lockCompany(
            jurisdiction,
            entityType
        );

        // Check fee
        require(
            msg.value == companyInfo.fee,
            ExceptionsLibrary.INCORRECT_ETH_PASSED
        );

        // Create pool
        IPool pool = IPool(getPoolAddress(companyInfo));

        // setNewOwnerWithSettings to pool contract
        pool.setNewOwnerWithSettings(msg.sender, trademark, governanceSettings);

        // Emit event
        emit PoolPurchased(address(pool), address(0), address(0));
        registry.log(
            msg.sender,
            address(this),
            msg.value,
            abi.encodeWithSelector(
                IService.purchasePool.selector,
                jurisdiction,
                entityType,
                trademark,
                governanceSettings
            )
        );
    }

    /**
     * @dev Method for purchasing a pool by the user. Among the data submitted for input, there are jurisdiction and Entity Type
     * @param jurisdiction jurisdiction
     * @param entityType entityType
     */
    function transferPurchasedPoolByService(
        address newowner,
        uint256 jurisdiction,
        uint256 entityType,
        string memory trademark,
        IGovernanceSettings.NewGovernanceSettings memory governanceSettings
    ) external onlyManager nonReentrant whenNotPaused {
        // Lock company
        IRegistry.CompanyInfo memory companyInfo = registry.lockCompany(
            jurisdiction,
            entityType
        );
        // Create pool
        IPool pool = IPool(getPoolAddress(companyInfo));

        // setNewOwnerWithSettings to pool contract
        pool.setNewOwnerWithSettings(newowner, trademark, governanceSettings);

        // Emit event
        emit PoolPurchased(address(pool), address(0), address(0));

        registry.log(
            msg.sender,
            address(this),
            0,
            abi.encodeWithSelector(
                IService.transferPurchasedPoolByService.selector,
                newowner,
                jurisdiction,
                entityType,
                trademark,
                governanceSettings
            )
        );
    }

    // PUBLIC INDIRECT FUNCTIONS (CALLED THROUGH POOL OR REGISTRY)

    /**
     * @dev Add proposal to directory
     * @param proposalId Proposal ID
     */
    function addProposal(uint256 proposalId) external onlyPool whenNotPaused {
        registry.addProposalRecord(msg.sender, proposalId);
    }

    /**
     * @dev Add event to directory
     * @param eventType Event type
     * @param proposalId Proposal ID
     * @param metaHash Hash value of event metadata
     */
    function addEvent(
        IRegistry.EventType eventType,
        uint256 proposalId,
        string calldata metaHash
    ) external onlyPool whenNotPaused {
        registry.addEventRecord(
            msg.sender,
            eventType,
            address(0),
            proposalId,
            metaHash
        );
    }

    function addInvoiceEvent(
        address pool,
        uint256 invoiceId
    ) external whenNotPaused returns (uint256) {
        require(
            msg.sender == address(invoice),
            ExceptionsLibrary.NOT_INVOICE_MANAGER
        );

        return
            registry.addEventRecord(
                pool,
                IRecordsRegistry.EventType.Transfer,
                msg.sender,
                invoiceId,
                ""
            );
    }

    /**
     * @dev Method for purchasing a pool by the user. Among the data submitted for input, there are jurisdiction and Entity Type, which are used as keys to, firstly, find out if there is a company available for acquisition with such parameters among the Registry records, and secondly, to get the data of such a company if it exists, save them to the deployed pool contract, while recording the company is removed from the Registry. This action is only available to users who are on the global white list of addresses allowed before the acquisition of companies. At the same time, the Governance token contract and the TGE contract are deployed for its implementation.
     * @param companyInfo Company info
     */
    function createPool(
        IRegistry.CompanyInfo memory companyInfo
    ) external onlyRegistry nonReentrant whenNotPaused {
        // Create pool
        IPool pool = _createPool(companyInfo);

        // Initialize pool contract
        pool.initialize(companyInfo);

        // Emit event
        emit PoolCreated(address(pool), address(0), address(0));
    }

    // RESTRICTED FUNCTIONS

    /**
     * @dev Transfer collected createPool protocol fees
     * @param to Transfer recipient
     */
    function transferCollectedFees(
        address to
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(to != address(0), ExceptionsLibrary.ADDRESS_ZERO);
        uint256 balance = payable(address(this)).balance;
        (bool success, ) = payable(to).call{value: balance}("");
        require(success, ExceptionsLibrary.EXECUTION_FAILED);
        emit FeesTransferred(to, balance);
    }

    /**
     * @dev Sets factories for previously deployed service
     * @param tokenFactory_ TokenFactory address
     * @param tgeFactory_ TGEFactory address
     */
    function setFactories(
        ITokenFactory tokenFactory_,
        ITGEFactory tgeFactory_
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        tokenFactory = tokenFactory_;
        tgeFactory = tgeFactory_;
    }

    /**
     * @dev Set protocol collected token fee
     * @param _token token address
     * @param _protocolTokenFee fee collected
     */
    function setProtocolCollectedFee(
        address _token,
        uint256 _protocolTokenFee
    ) public onlyTGE {
        protolCollectedFee[_token] += _protocolTokenFee;
    }

    /**
     * @dev Assignment of the address to which the commission will be collected in the form of Governance tokens issued under successful TGE
     * @param _protocolTreasury Protocol treasury address
     */
    function setProtocolTreasury(
        address _protocolTreasury
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        require(
            _protocolTreasury != address(0),
            ExceptionsLibrary.ADDRESS_ZERO
        );

        protocolTreasury = _protocolTreasury;
        emit ProtocolTreasuryChanged(protocolTreasury);
    }

    /**
     * @dev Set protocol token fee
     * @param _protocolTokenFee protocol token fee percentage value with 4 decimals.
     * Examples: 1% = 10000, 100% = 1000000, 0.1% = 1000.
     */
    function setProtocolTokenFee(
        uint256 _protocolTokenFee
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_protocolTokenFee <= DENOM, ExceptionsLibrary.INVALID_VALUE);

        protocolTokenFee = _protocolTokenFee;
        emit ProtocolTokenFeeChanged(_protocolTokenFee);
    }

    /**
     * @dev Sets new Registry contract
     * @param _registry registry address
     */
    function setRegistry(
        IRegistry _registry
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(
            address(_registry) != address(0),
            ExceptionsLibrary.ADDRESS_ZERO
        );

        registry = _registry;
        emit RegistryChanged(address(registry));
    }

    /**
     * @dev Sets new customProposal contract
     * @param _customProposal customProposal address
     */
    function setCustomProposal(
        ICustomProposal _customProposal
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(
            address(_customProposal) != address(0),
            ExceptionsLibrary.ADDRESS_ZERO
        );

        customProposal = _customProposal;
        emit CustomPropsalChanged(address(customProposal));
    }

    /**
     * @dev Sets new vesting
     * @param _vesting vesting address
     */
    function setVesting(
        IVesting _vesting
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(
            address(_vesting) != address(0),
            ExceptionsLibrary.ADDRESS_ZERO
        );

        vesting = _vesting;
        emit VestingChanged(address(vesting));
    }

    /**
     * @dev Sets new invoice contract
     * @param _invoice invoice address
     */
    function setInvoice(
        IInvoice _invoice
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(
            address(_invoice) != address(0),
            ExceptionsLibrary.ADDRESS_ZERO
        );

        invoice = _invoice;
        emit InvoiceChanged(address(invoice));
    }

    /**
     * @dev Sets new pool beacon
     * @param beacon Beacon address
     */
    function setPoolBeacon(
        address beacon
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(beacon != address(0), ExceptionsLibrary.ADDRESS_ZERO);

        poolBeacon = beacon;
        emit PoolBeaconChanged(address(poolBeacon));
    }

    /**
     * @dev Sets new token beacon
     * @param beacon Beacon address
     */
    function setTokenBeacon(
        address beacon
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(beacon != address(0), ExceptionsLibrary.ADDRESS_ZERO);

        tokenBeacon = beacon;
        emit TokenBeaconChanged(address(tokenBeacon));
    }

    /**
     * @dev Sets new tokenERC1155 beacon
     * @param beacon Beacon address
     */
    function setTokenERC1155Beacon(
        address beacon
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(beacon != address(0), ExceptionsLibrary.ADDRESS_ZERO);

        tokenERC1155Beacon = beacon;
    }

    /**
     * @dev Sets new TGE beacon
     * @param beacon Beacon address
     */
    function setTGEBeacon(
        address beacon
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(beacon != address(0), ExceptionsLibrary.ADDRESS_ZERO);

        tgeBeacon = beacon;
        emit TGEBeaconChanged(address(tgeBeacon));
    }

    /**
     * @dev Cancel pool's proposal
     * @param pool pool
     * @param proposalId proposalId
     */
    function cancelProposal(
        address pool,
        uint256 proposalId
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        IPool(pool).cancelProposal(proposalId);
        emit ProposalCancelled(pool, proposalId);
    }

    /**
     * @dev Pause service
     */
    function pause() public onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    /**
     * @dev Unpause service
     */
    function unpause() public onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    // PUBLIC VIEW FUNCTIONS

    /**
     * @dev Returns true if the contract is paused, and false otherwise.
     */
    function paused()
        public
        view
        override(PausableUpgradeable, IService)
        returns (bool)
    {
        return super.paused();
    }

    /**
     * @dev Calculate minimum soft cap for token fee mechanism to work
     * @return softCap minimum soft cap
     */
    function getMinSoftCap() public view returns (uint256) {
        return (DENOM + protocolTokenFee - 1) / protocolTokenFee;
    }

    /**
     * @dev alculates protocol token fee for given token amount
     * @param amount Token amount
     * @return tokenFee
     */
    function getProtocolTokenFee(uint256 amount) public view returns (uint256) {
        require(amount >= getMinSoftCap(), ExceptionsLibrary.INVALID_VALUE);
        return (amount * protocolTokenFee + (DENOM - 1)) / DENOM;
    }

    /**
     * @dev Returns protocol token fee claimed for given token
     * @param token_ Token adress
     * @return token claimed
     */
    function getProtocolCollectedFee(
        address token_
    ) external view returns (uint256) {
        return protolCollectedFee[token_];
    }

    /**
     * @dev Return max hard cap accounting for protocol token fee
     * @param _pool pool to calculate hard cap against
     * @return Maximum hard cap
     */
    function getMaxHardCap(address _pool) public view returns (uint256) {
        if (
            registry.typeOf(_pool) == IRecordsRegistry.ContractType.Pool &&
            IPool(_pool).isDAO()
        ) {
            return
                IPool(_pool).getGovernanceToken().cap() -
                getProtocolTokenFee(IPool(_pool).getGovernanceToken().cap());
        }

        return type(uint256).max - getProtocolTokenFee(type(uint256).max);
    }

    /// @dev Service function that is used to check the correctness of TGE parameters (for the absence of conflicts between parameters)
    function validateTGEInfo(
        ITGE.TGEInfo calldata info,
        uint256 cap,
        uint256 totalSupplyWithReserves,
        IToken.TokenType tokenType
    ) external view {
        // Check unit of account
        if (info.unitOfAccount != address(0))
            require(
                IERC20Upgradeable(info.unitOfAccount).totalSupply() > 0,
                ExceptionsLibrary.INVALID_TOKEN
            );

        // Check hardcap
        require(
            info.hardcap >= info.softcap,
            ExceptionsLibrary.INVALID_HARDCAP
        );

        // Check vesting params
        vesting.validateParams(info.vestingParams);

        // Check remaining supply
        uint256 remainingSupply = cap - totalSupplyWithReserves;
        require(
            info.hardcap <= remainingSupply,
            ExceptionsLibrary.HARDCAP_OVERFLOW_REMAINING_SUPPLY
        );
        if (tokenType == IToken.TokenType.Governance) {
            require(
                info.hardcap + getProtocolTokenFee(info.hardcap) <=
                    remainingSupply,
                ExceptionsLibrary
                    .HARDCAP_AND_PROTOCOL_FEE_OVERFLOW_REMAINING_SUPPLY
            );
        }
    }

    /**
     * @dev Get's create2 address for pool
     * @param info Company info
     * @return Pool contract address
     */
    function getPoolAddress(
        IRegistry.CompanyInfo memory info
    ) public view returns (address) {
        (bytes32 salt, bytes memory bytecode) = _getCreate2Data(info);
        return Create2Upgradeable.computeAddress(salt, keccak256(bytecode));
    }

    // INTERNAL FUNCTIONS

    /**
     * @dev Gets data for pool's create2
     * @param info Company info
     * @return salt Create2 salt
     * @return deployBytecode Deployed bytecode
     */
    function _getCreate2Data(
        IRegistry.CompanyInfo memory info
    ) internal view returns (bytes32 salt, bytes memory deployBytecode) {
        // Get salt
        salt = keccak256(
            abi.encode(info.jurisdiction, info.entityType, info.ein)
        );

        // Get bytecode
        bytes memory proxyBytecode = type(BeaconProxy).creationCode;
        deployBytecode = abi.encodePacked(
            proxyBytecode,
            abi.encode(poolBeacon, "")
        );
    }

    /**
     * @dev Create pool contract and initialize it
     * @return pool Pool contract
     */
    function _createPool(
        IRegistry.CompanyInfo memory info
    ) internal returns (IPool pool) {
        // Create pool contract using Create2
        (bytes32 salt, bytes memory bytecode) = _getCreate2Data(info);
        pool = IPool(Create2Upgradeable.deploy(0, salt, bytecode));

        // Add pool contract to registry
        registry.addContractRecord(
            address(pool),
            IRecordsRegistry.ContractType.Pool,
            ""
        );
    }
}

contracts/TGE.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/utils/math/MathUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "./interfaces/IToken.sol";
import "./interfaces/ITokenERC1155.sol";
import "./interfaces/ITGE.sol";
import "./interfaces/IService.sol";
import "./interfaces/IPool.sol";
import "./interfaces/IVesting.sol";
import "./libraries/ExceptionsLibrary.sol";

import "./interfaces/IPausable.sol";
/// @title Token Generation Event
/// @dev A contract whose purpose is to distribute Governance and Preference tokens and ensure their blocking according to the settings. This contract has an active period, after which the sale of tokens stops, but additional rules related to this token - Lockup and Vesting - begin to apply.  Such a contract is considered successful if at least softcap tokens have been sold using it in the allotted time. Dependencies of TGE contracts on each other for one token - 1) before there is at least one successfully completed TGE, each subsequent created TGE is considered primary (including the very first for the token), 2) if there was at least one successful TGE for an existing token before the launch of a new TGE, then the created TGE is called secondary (and does not have a softcap, that is, any purchase makes it successful).

contract TGE is Initializable, ReentrancyGuardUpgradeable, ITGE {
    using AddressUpgradeable for address payable;
    using SafeERC20Upgradeable for IERC20Upgradeable;

    // CONSTANTS

    /// @notice Denominator for shares (such as thresholds)
    uint256 private constant DENOM = 100 * 10 ** 4;

    /// @dev Pool's ERC20/ERC1155 token
    address public token;

    /// @dev Pool's ERC1155 tokenId
    uint256 public tokenId;

    /// @dev TGE info struct
    TGEInfo public info;

    /// @dev Mapping of user's address to whitelist status
    mapping(address => bool) private _isUserWhitelisted;

    /// @dev Block of TGE's creation
    uint256 public createdAt;

    /// @dev Mapping of an address to total amount of tokens purchased during TGE
    mapping(address => uint256) public purchaseOf;

    /// @dev Total amount of tokens purchased during TGE
    uint256 public totalPurchased;

    /// @dev Is vesting TVL reached. Users can claim their tokens only if vesting TVL was reached.
    bool public vestingTVLReached;

    /// @dev Is lockup TVL reached. Users can claim their tokens only if lockup TVL was reached.
    bool public lockupTVLReached;

    /// @dev Mapping of addresses to total amounts of tokens vested
    mapping(address => uint256) public vestedBalanceOf;

    /// @dev Total amount of tokens vested
    uint256 public totalVested;

    /// @dev Protocol fee
    uint256 public protocolFee;

    /// @dev Protocol token fee is a percentage of tokens sold during TGE. Returns true if fee was claimed by the governing DAO.
    bool public isProtocolTokenFeeClaimed;

    /// @dev Total amount of tokens reserved for protocolfee
    uint256 public totalProtocolFee;

    /// @dev Vesting contract
    IVesting public vesting;

    // EVENTS

    /**
     * @dev Event emitted on token purchase.
     * @param buyer buyer
     * @param amount amount of tokens
     */
    event Purchased(address buyer, uint256 amount);

    /**
     * @dev Event emitted on claim of protocol token fee.
     * @param token token
     * @param tokenFee amount of tokens
     */
    event ProtocolTokenFeeClaimed(address token, uint256 tokenFee);

    /**
     * @dev Event emitted on token claim.
     * @param account Redeemer address
     * @param refundValue Refund value
     */
    event Redeemed(address account, uint256 refundValue);

    /**
     * @dev Event emitted on transfer funds to pool.
     * @param amount Amount of transferred tokens/ETH
     */
    event FundsTransferred(uint256 amount);

    // INITIALIZER AND CONSTRUCTOR

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Constructor function, can only be called once. In this method, settings for the TGE event are assigned, such as the contract of the token implemented using TGE, as well as the TGEInfo structure, which includes the parameters of purchase, vesting and lockup. If no lockup or westing conditions were set for the TVL value when creating the TGE, then the TVL achievement flag is set to true from the very beginning.
     * @param _service Service contract
     * @param _token TGE's token
     * @param _tokenId TGE's tokenId
     * @param _info TGE parameters
     * @param _protocolFee Protocol's fee value
     */
    function initialize(
        address _service,
        address _token,
        uint256 _tokenId,
        string memory _uri,
        TGEInfo calldata _info,
        uint256 _protocolFee
    ) external initializer {
        __ReentrancyGuard_init();

        //if tge is creating for erc20 token
        tokenId = _tokenId;
        if (tokenId == 0) {
            IService(_service).validateTGEInfo(
                _info,
                IToken(_token).cap(),
                IToken(_token).totalSupplyWithReserves(),
                IToken(_token).tokenType()
            );
        } else {
            //if tge is creating for erc155 token
            if (ITokenERC1155(_token).cap(tokenId) != 0) {
                IService(_service).validateTGEInfo(
                    _info,
                    ITokenERC1155(_token).cap(tokenId),
                    ITokenERC1155(_token).totalSupplyWithReserves(tokenId),
                    IToken(_token).tokenType()
                );
            }
            ITokenERC1155(_token).setURI(_tokenId, _uri);
        }
        vesting = IService(_service).vesting();
        token = _token;

        info = _info;
        protocolFee = _protocolFee;
        lockupTVLReached = (_info.lockupTVL == 0);

        for (uint256 i = 0; i < _info.userWhitelist.length; i++) {
            _isUserWhitelisted[_info.userWhitelist[i]] = true;
        }

        createdAt = block.number;
    }

    // PUBLIC FUNCTIONS

    /**
     * @dev Purchase pool's tokens during TGE. The method for users from the TGE whitelist (set in TGEInfo when initializing the TGE contract), if the list is not set, then the sale is carried out for any address. The contract, when using this method, exchanges info.unitofaccount tokens available to buyers (if the address of the info.unitofaccount contract is set as zero, then the native ETH is considered unitofaccount) at the info.price rate of info.unitofaccount tokens for one pool token being sold. The buyer specifies the purchase amount in pool tokens, not in Unitofaccount. After receiving the user's funds by the contract, part of the tokens are minted to the buyer's balance, part of the tokens are minted to the address of the TGE contract in vesting. The percentage of vesting is specified in info.vestingPercent, if it is equal to 0, then all tokens are transferred to the buyer's balance.
     * @param amount amount of tokens in wei (10**18 = 1 token)
     */
    function purchase(
        uint256 amount
    )
        external
        payable
        onlyWhitelistedUser
        onlyState(State.Active)
        nonReentrant
        whenPoolNotPaused
    {
        // Check purchase price transfer depending on unit of account
        address unitOfAccount = info.unitOfAccount;
        uint256 purchasePrice = (amount * info.price + (1 ether - 1)) / 1 ether;
        if (unitOfAccount == address(0)) {
            require(
                msg.value >= purchasePrice,
                ExceptionsLibrary.INCORRECT_ETH_PASSED
            );
        } else {
            IERC20Upgradeable(unitOfAccount).safeTransferFrom(
                msg.sender,
                address(this),
                purchasePrice
            );
        }

        // Check purchase size
        require(
            amount >= info.minPurchase,
            ExceptionsLibrary.MIN_PURCHASE_UNDERFLOW
        );
        require(
            amount <= maxPurchaseOf(msg.sender),
            ExceptionsLibrary.MAX_PURCHASE_OVERFLOW
        );

        // Accrue TGE stats
        totalPurchased += amount;
        purchaseOf[msg.sender] += amount;

        // Mint tokens directly to user
        uint256 vestedAmount = (amount *
            info.vestingParams.vestedShare +
            (DENOM - 1)) / DENOM;

        if (amount - vestedAmount > 0) {
            if (isERC1155TGE()) {
                ITokenERC1155(token).mint(
                    msg.sender,
                    tokenId,
                    amount - vestedAmount
                );
            } else {
                IToken(token).mint(msg.sender, amount - vestedAmount);
            }
        }

        // Vest tokens
        if (vestedAmount > 0) {
            if (isERC1155TGE()) {
                ITokenERC1155(token).setTGEVestedTokens(
                    ITokenERC1155(token).getTotalTGEVestedTokens(tokenId) +
                        vestedAmount,
                    tokenId
                );
            } else {
                IToken(token).setTGEVestedTokens(
                    IToken(token).getTotalTGEVestedTokens() + vestedAmount
                );
            }

            vesting.vest(msg.sender, vestedAmount);
        }

        // Increase reserved protocol fee
        uint256 tokenFee = getProtocolTokenFee(amount);
        if (tokenFee > 0) {
            totalProtocolFee += tokenFee;
            if (isERC1155TGE()) {
                ITokenERC1155(token).setProtocolFeeReserved(
                    ITokenERC1155(token).getTotalProtocolFeeReserved(tokenId) +
                        tokenFee,
                    tokenId
                );
            } else {
                IToken(token).setProtocolFeeReserved(
                    IToken(token).getTotalProtocolFeeReserved() + tokenFee
                );
            }
        }

        // Emit event
        emit Purchased(msg.sender, amount);

        IToken(token).service().registry().log(
            msg.sender,
            address(this),
            0,
            abi.encodeWithSelector(ITGE.purchase.selector, amount)
        );

    }

    /**
     * @dev Return purchased tokens and get back tokens paid. This method allows buyers of TGE who have not collected softcap and are considered failed to return the funds spent by handing back the purchased tokens. The refund of funds for tokens from vesting, blocked within this TGE, is made first of all. The buyer cannot hand over more tokens than he acquired during this TGE. The tokens returned to the contract are sent for burning.
     */
    function redeem()
        external
        onlyState(State.Failed)
        nonReentrant
        whenPoolNotPaused
    {
        // User can't claim more than he bought in this event (in case somebody else has transferred him tokens)
        require(
            purchaseOf[msg.sender] > 0,
            ExceptionsLibrary.ZERO_PURCHASE_AMOUNT
        );

        uint256 refundAmount = 0;

        // Calculate redeem from vesting
        uint256 vestedBalance = vesting.vested(address(this), msg.sender);
        if (vestedBalance > 0) {
            // Account vested tokens
            purchaseOf[msg.sender] -= vestedBalance;
            refundAmount += vestedBalance;

            // Cancel vesting
            vesting.cancel(address(this), msg.sender);

            // Decrease reserved tokens
            if (isERC1155TGE()) {
                ITokenERC1155(token).setTGEVestedTokens(
                    ITokenERC1155(token).getTotalTGEVestedTokens(tokenId) -
                        vestedBalance,
                    tokenId
                );
            } else {
                IToken(token).setTGEVestedTokens(
                    IToken(token).getTotalTGEVestedTokens() - vestedBalance
                );
            }
        }

        // Calculate redeemed balance
        uint256 balanceToRedeem;
        if (isERC1155TGE()) {
            balanceToRedeem = MathUpgradeable.min(
                ITokenERC1155(token).balanceOf(msg.sender, tokenId),
                purchaseOf[msg.sender]
            );
        } else {
            balanceToRedeem = MathUpgradeable.min(
                IToken(token).balanceOf(msg.sender),
                purchaseOf[msg.sender]
            );
        }
        if (balanceToRedeem > 0) {
            purchaseOf[msg.sender] -= balanceToRedeem;
            refundAmount += balanceToRedeem;
            if (isERC1155TGE()) {
                ITokenERC1155(token).burn(msg.sender, tokenId, balanceToRedeem);
            } else {
                IToken(token).burn(msg.sender, balanceToRedeem);
            }
        }

        // Check that there is anything to refund
        require(refundAmount > 0, ExceptionsLibrary.NOTHING_TO_REDEEM);

        // Transfer refund value
        uint256 refundValue = (refundAmount * info.price + (1 ether - 1)) /
            1 ether;
        if (info.unitOfAccount == address(0)) {
            payable(msg.sender).sendValue(refundValue);
        } else {
            IERC20Upgradeable(info.unitOfAccount).safeTransfer(
                msg.sender,
                refundValue
            );
        }

        // Decrease reserved protocol fee
        uint256 tokenFee = getProtocolTokenFee(refundAmount);
        if (tokenFee > 0) {
            totalProtocolFee -= tokenFee;
            if (isERC1155TGE()) {
                ITokenERC1155(token).setProtocolFeeReserved(
                    ITokenERC1155(token).getTotalProtocolFeeReserved(tokenId) -
                        tokenFee,
                    tokenId
                );
            } else {
                IToken(token).setProtocolFeeReserved(
                    IToken(token).getTotalProtocolFeeReserved() - tokenFee
                );
            }
        }

        // Emit event
        emit Redeemed(msg.sender, refundValue);
    }

    /// @dev Set the flag that the condition for achieving the pool balance of the value specified in the lockup settings is met. The action is irreversible.
    function setLockupTVLReached()
        external
        whenPoolNotPaused
        onlyManager
        onlyState(State.Successful)
    {
        // Check that TVL has not been reached yet
        require(!lockupTVLReached, ExceptionsLibrary.LOCKUP_TVL_REACHED);

        // Mark as reached
        lockupTVLReached = true;
    }

    // RESTRICTED FUNCTIONS

    /**
     * @dev This method is used to perform the following actions for a successful TGE after its completion: transfer funds collected from buyers in the form of info.unitofaccount tokens or ETH to the address of the pool to which TGE belongs (if info.price is 0, then this action is not performed), as well as for Governance tokens make a minting of the percentage of the amount of all user purchases specified in the Service.sol protocolTokenFee contract and transfer it to the address specified in the Service.sol contract in the protocolTreasury() getter. Can be executed only once. Any address can call the method.
     */
    function transferFunds()
        external
        onlyState(State.Successful)
        whenPoolNotPaused
    {
        // Return if nothing to transfer
        if (totalPurchased == 0) {
            return;
        }

        // Claim protocol fee
        _claimProtocolTokenFee();

        // Transfer remaining funds to pool
        address unitOfAccount = info.unitOfAccount;

        address pool = IToken(token).pool();

        uint256 balance = 0;
        if (info.price != 0) {
            if (unitOfAccount == address(0)) {
                balance = address(this).balance;
                payable(pool).sendValue(balance);
            } else {
                balance = IERC20Upgradeable(unitOfAccount).balanceOf(
                    address(this)
                );
                IERC20Upgradeable(unitOfAccount).safeTransfer(pool, balance);
            }
        }

        // Emit event
        emit FundsTransferred(balance);



        IToken(token).service().registry().log(
            msg.sender,
            address(this),
            0,
            abi.encodeWithSelector(ITGE.transferFunds.selector)
        );
    }

    /// @dev Transfers protocol token fee in form of pool's governance tokens to protocol treasury
    function _claimProtocolTokenFee() private {
        // Return if already claimed
        if (isProtocolTokenFeeClaimed) {
            return;
        }

        // Return for preference token
        if (IToken(token).tokenType() == IToken.TokenType.Preference) {
            return;
        }

        // Mark fee as claimed
        isProtocolTokenFeeClaimed = true;

        // Mint fee to treasury
        uint256 tokenFee = totalProtocolFee;
        if (totalProtocolFee > 0) {
            totalProtocolFee = 0;
            if (isERC1155TGE()) {
                ITokenERC1155(token).mint(
                    ITokenERC1155(token).service().protocolTreasury(),
                    tokenId,
                    tokenFee
                );
                ITokenERC1155(token).setProtocolFeeReserved(
                    ITokenERC1155(token).getTotalProtocolFeeReserved(tokenId) -
                        tokenFee,
                    tokenId
                );
            } else {
                IToken(token).mint(
                    IToken(token).service().protocolTreasury(),
                    tokenFee
                );
                IToken(token).setProtocolFeeReserved(
                    IToken(token).getTotalProtocolFeeReserved() - tokenFee
                );
            }
        }

        // Emit event
        emit ProtocolTokenFeeClaimed(token, tokenFee);
    }

    // VIEW FUNCTIONS

    /**
     * @dev Shows the maximum possible number of tokens to be purchased by a specific address, taking into account whether the user is on the white list and 0 what amount of purchases he made within this TGE.
     * @return Amount of tokens
     */
    function maxPurchaseOf(address account) public view returns (uint256) {
        if (!isUserWhitelisted(account)) {
            return 0;
        }
        return
            MathUpgradeable.min(
                info.maxPurchase - purchaseOf[account],
                info.hardcap - totalPurchased
            );
    }

    /**
     * @dev The Getter allows you to find out the status of the current TGE. Usually, the status necessarily changes to 1 - "failure" or 2 - "success" at the end of the TGE validity period, depending on whether the softcap failed or was collected. However, if the buyers purchased 100% of the tokens offered for purchase, the status changes ahead of time to 2 - "success". The active TGE (which has not expired and has not collected hardcap) has the status 0. Changing the status from 0 to 1 or 2 is irreversible.
     * @return State
     */
    function state() public view returns (State) {
        // If hardcap is reached TGE is successfull
        if (totalPurchased == info.hardcap) {
            return State.Successful;
        }

        // If deadline not reached TGE is active
        if (block.number < createdAt + info.duration) {
            return State.Active;
        }

        // If it's not primary TGE it's successfull (if anything is purchased)
        if (isERC1155TGE()) {
            if (
                address(this) != ITokenERC1155(token).getTGEList(tokenId)[0] &&
                totalPurchased > 0
            ) {
                return State.Successful;
            }
        } else {
            if (
                address(this) != IToken(token).getTGEList()[0] &&
                totalPurchased > 0
            ) {
                return State.Successful;
            }
        }

        // If softcap is reached TGE is successfull
        if (totalPurchased >= info.softcap && totalPurchased > 0) {
            return State.Successful;
        }

        // Otherwise it's failed primary TGE
        return State.Failed;
    }

    /**
     * @dev The given getter shows whether the transfer method is available for tokens that were distributed using a specific TGE contract. If the lockup period is over or if the lockup was not provided for this TGE, the getter always returns true.
     * @return Is transfer available
     */
    function transferUnlocked() public view returns (bool) {
        return
            lockupTVLReached && block.number >= createdAt + info.lockupDuration;
    }

    /**
     * @dev Shows the number of TGE tokens blocked in this contract. If the lockup is completed or has not been assigned, the method returns 0 (all tokens on the address balance are available for transfer). If the lockup period is still active, then the difference between the tokens purchased by the user and those in the vesting is shown (both parameters are only for this TGE).
     * @param account Account address
     * @return Locked balance
     */
    function lockedBalanceOf(address account) external view returns (uint256) {
        return
            transferUnlocked()
                ? 0
                : (purchaseOf[account] -
                    vesting.vestedBalanceOf(address(this), account));
    }

    /**
     * @dev Shows the number of TGE tokens available for redeem for `account`
     * @param account Account address
     * @return Redeemable balance of the address
     */
    function redeemableBalanceOf(
        address account
    ) external view returns (uint256) {
        if (purchaseOf[account] == 0) return 0;
        if (state() != State.Failed) return 0;

        if (isERC1155TGE()) {
            return
                MathUpgradeable.min(
                    ITokenERC1155(token).balanceOf(account, tokenId) +
                        vesting.vestedBalanceOf(address(this), account),
                    purchaseOf[account]
                );
        } else {
            return
                MathUpgradeable.min(
                    IToken(token).balanceOf(account) +
                        vesting.vestedBalanceOf(address(this), account),
                    purchaseOf[account]
                );
        }
    }

    /**
     * @dev The given getter shows how much info.unitofaccount was collected within this TGE. To do this, the amount of tokens purchased by all buyers is multiplied by info.price.
     * @return Total value
     */
    function getTotalPurchasedValue() public view returns (uint256) {
        return (totalPurchased * info.price) / 10 ** 18;
    }

    /**
     * @dev This getter shows the total value of all tokens that are in the vesting. Tokens that were transferred to users wallet addresses upon request for successful TGEs and that were burned as a result of user funds refund for unsuccessful TGEs are not taken into account.
     * @return Total value
     */
    function getTotalVestedValue() public view returns (uint256) {
        return (vesting.totalVested(address(this)) * info.price) / 10 ** 18;
    }

    /**
     * @dev Get userwhitelist info
     * @return User whitelist
     */
    function getUserWhitelist() external view returns (address[] memory) {
        return info.userWhitelist;
    }

    /**
     * @dev Checks if user is whitelisted
     * @param account User address
     * @return Flag if user if whitelisted
     */
    function isUserWhitelisted(address account) public view returns (bool) {
        return info.userWhitelist.length == 0 || _isUserWhitelisted[account];
    }

    /**
     * @dev Checks if TGE is created for ERC1155
     * @return Flag if ERC1155 TGE
     */
    function isERC1155TGE() public view returns (bool) {
        return tokenId == 0 ? false : true;
    }

    /**
     * @dev Get TGE end block
     * @return TGE end block
     */
    function getEnd() external view returns (uint256) {
        return createdAt + info.duration;
    }

    /**
     * @dev Get TGE info
     * @return TGE info
     */
    function getInfo() external view returns (TGEInfo memory) {
        return info;
    }

    /*
     * @dev alculates protocol token fee for given token amount
     * @param amount Token amount
     * @return tokenFee
     */
    function getProtocolTokenFee(uint256 amount) public view returns (uint256) {
        if (IToken(token).tokenType() == IToken.TokenType.Preference) {
            return 0;
        }
        return (amount * protocolFee + (DENOM - 1)) / DENOM;
    }

    // MODIFIER

    modifier onlyState(State state_) {
        require(state() == state_, ExceptionsLibrary.WRONG_STATE);
        _;
    }

    modifier onlyWhitelistedUser() {
        require(
            isUserWhitelisted(msg.sender),
            ExceptionsLibrary.NOT_WHITELISTED
        );
        _;
    }

    modifier onlyManager() {
        IService service = IToken(token).service();
        require(
            service.hasRole(service.SERVICE_MANAGER_ROLE(), msg.sender),
            ExceptionsLibrary.NOT_WHITELISTED
        );
        _;
    }

    modifier whenPoolNotPaused() {
        require(
            !IPausable(IToken(token).pool()).paused(),
            ExceptionsLibrary.SERVICE_PAUSED
        );
        _;
    }
}

contracts/TGEFactory.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts/proxy/beacon/BeaconProxy.sol";
import "./interfaces/IService.sol";
import "./interfaces/IToken.sol";
import "./interfaces/ITGE.sol";
import "./interfaces/IPool.sol";
import "./interfaces/ITGEFactory.sol";
import "./interfaces/governor/IGovernanceSettings.sol";
import "./libraries/ExceptionsLibrary.sol";

contract TGEFactory is ReentrancyGuardUpgradeable, ITGEFactory {
    // STORAGE

    /// @notice Service contract
    IService public service;

    // EVENTS

    /**
     * @dev Event emitted on creation of primary TGE.
     * @param pool Pool address
     * @param tge Secondary TGE address
     * @param token Preference token address
     */
    event PrimaryTGECreated(address pool, address tge, address token);

    /**
     * @dev Event emitted on creation of secondary TGE.
     * @param pool Pool address
     * @param tge Secondary TGE address
     * @param token Preference token address
     */
    event SecondaryTGECreated(address pool, address tge, address token);

    /**
     * @dev Event emitted on creation of secondary TGE.
     * @param pool Pool address
     * @param tge Secondary TGE address
     * @param token Preference token address
     * @param token Preference tokenId
     */
    event SecondaryTGEERC1155Created(
        address pool,
        address tge,
        address token,
        uint256 tokenId
    );

    // MODIFIERS

    modifier onlyPool() {
        require(
            service.registry().typeOf(msg.sender) ==
                IRecordsRegistry.ContractType.Pool,
            ExceptionsLibrary.NOT_POOL
        );
        _;
    }

    modifier whenNotPaused() {
        require(!service.paused(), ExceptionsLibrary.SERVICE_PAUSED);
        _;
    }

    // INITIALIZER

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Initializer function, can only be called once
     * @param service_ Service address
     */
    function initialize(IService service_) external initializer {
        __ReentrancyGuard_init();
        service = service_;
    }

    // EXTERNAL FUNCTIONS

    /**
     * @dev Method for launching primary TGE for tokens
     * @param poolAddress Pool address.
     * @param tokenInfo New token parameters (token type, decimals & description are ignored)
     * @param tgeInfo Pool TGE parameters
     * @param metadataURI Metadata URI
     */
    function createPrimaryTGE(
        address poolAddress,
        IToken.TokenInfo memory tokenInfo,
        ITGE.TGEInfo memory tgeInfo,
        string memory metadataURI,
        IGovernanceSettings.NewGovernanceSettings memory governanceSettings_,
        address[] memory secretary,
        address[] memory executor
    ) external nonReentrant whenNotPaused {
        // Check that sender is pool owner
        IPool pool = IPool(poolAddress);
        require(pool.owner() == msg.sender, ExceptionsLibrary.NOT_POOL_OWNER);

        // Check token cap
        require(tokenInfo.cap >= 1 ether, ExceptionsLibrary.INVALID_CAP);

        // Check that pool is not active yet
        require(
            address(pool.getGovernanceToken()) == address(0) || !pool.isDAO(),
            ExceptionsLibrary.GOVERNANCE_TOKEN_EXISTS
        );
        pool.setSettings(governanceSettings_, secretary, executor);

        // Create TGE contract
        ITGE tge = _createTGE(metadataURI, address(pool));

        // Create token contract
        tokenInfo.tokenType = IToken.TokenType.Governance;
        tokenInfo.decimals = 18;
        address token = service.tokenFactory().createToken(
            address(pool),
            tokenInfo,
            address(tge)
        );

        // Set token as pool token
        pool.setToken(address(token), IToken.TokenType.Governance);

        // Initialize TGE
        tge.initialize(
            address(service),
            address(token),
            0,
            "",
            tgeInfo,
            service.protocolTokenFee()
        );
        emit PrimaryTGECreated(address(pool), address(tge), address(token));

        service.registry().log(
            msg.sender,
            address(this),
            0,
            abi.encodeWithSelector(
                ITGEFactory.createPrimaryTGE.selector,
                poolAddress,
                tokenInfo,
                tgeInfo,
                metadataURI,
                governanceSettings_,
                secretary,
                executor
            )
        );
    }

    /**
     * @dev Method for launching secondary TGE (i.e. without reissuing the token) for Governance tokens, as well as for creating and launching TGE for Preference tokens. It can be started only as a result of the execution of the proposal on behalf of the pool.
     * @param tgeInfo TGE parameters
     * @param tokenInfo Token parameters
     * @param metadataURI Metadata URI
     */
    function createSecondaryTGE(
        address token,
        ITGE.TGEInfo calldata tgeInfo,
        IToken.TokenInfo calldata tokenInfo,
        string memory metadataURI
    ) external onlyPool nonReentrant whenNotPaused {
        ITGE tge;
        // Check whether it's initial preference TGE or any secondary token
        if (
            tokenInfo.tokenType == IToken.TokenType.Preference &&
            address(token) == address(0)
        ) {
            (token, tge) = _createInitialPreferenceTGE(
                token,
                0,
                "",
                tgeInfo,
                tokenInfo,
                metadataURI
            );
        } else {
            (token, tge) = _createSecondaryTGE(
                token,
                0,
                "",
                tgeInfo,
                tokenInfo,
                metadataURI
            );
        }

        // Add proposal id to TGE
        IPool(msg.sender).setProposalIdToTGE(address(tge));

        // Emit event
        emit SecondaryTGECreated(msg.sender, address(tge), address(token));
    }

    /**
     * @dev Method for launching secondary TGE for ERC1155.
     * @param tgeInfo TGE parameters
     * @param tokenInfo Token parameters
     * @param metadataURI Metadata URI
     */
    function createSecondaryTGEERC1155(
        address token,
        uint256 tokenId,
        string memory uri,
        ITGE.TGEInfo calldata tgeInfo,
        IToken.TokenInfo calldata tokenInfo,
        string memory metadataURI
    ) external onlyPool nonReentrant whenNotPaused {
        require(
            tokenInfo.tokenType == IToken.TokenType.Preference,
            ExceptionsLibrary.WRONG_STATE
        );
        ITGE tge;
        // Check whether it's initial preference TGE or any secondary token
        if (address(token) == address(0)) {
            (token, tge) = _createInitialPreferenceTGE(
                token,
                tokenId,
                uri,
                tgeInfo,
                tokenInfo,
                metadataURI
            );
        } else {
            (token, tge) = _createSecondaryTGE(
                token,
                tokenId,
                uri,
                tgeInfo,
                tokenInfo,
                metadataURI
            );
        }
        if (ITokenERC1155(token).cap(tokenId) == 0) {
            ITokenERC1155(token).setTokenIdCap(tokenId, tokenInfo.cap);
        }

        // Add proposal id to TGE
        IPool(msg.sender).setProposalIdToTGE(address(tge));

        // Emit event
        emit SecondaryTGEERC1155Created(
            msg.sender,
            address(tge),
            address(token),
            tokenId
        );
    }

    // INTERNAL FUNCTIONS

    function _createSecondaryTGE(
        address token,
        uint256 tokenId,
        string memory uri,
        ITGE.TGEInfo calldata tgeInfo,
        IToken.TokenInfo calldata tokenInfo,
        string memory metadataURI
    ) internal returns (address, ITGE) {
        // Check that token is valid
        require(
            tokenInfo.tokenType != IToken.TokenType.None &&
                IPool(msg.sender).tokenTypeByAddress(address(token)) ==
                tokenInfo.tokenType,
            ExceptionsLibrary.WRONG_TOKEN_ADDRESS
        );

        // Check that there is no active TGE
        if (tokenId != 0) {
            require(
                ITGE(ITokenERC1155(token).lastTGE(tokenId)).state() !=
                    ITGE.State.Active,
                ExceptionsLibrary.ACTIVE_TGE_EXISTS
            );
        } else {
            require(
                ITGE(IToken(token).lastTGE()).state() != ITGE.State.Active,
                ExceptionsLibrary.ACTIVE_TGE_EXISTS
            );
        }
        // Create TGE
        ITGE tge = _createTGE(metadataURI, msg.sender);

        // Add TGE to token's list
        IToken(token).addTGE(address(tge));

        // Get protocol fee
        uint256 protocolTokenFee = tokenInfo.tokenType ==
            IToken.TokenType.Governance
            ? service.protocolTokenFee()
            : 0;

        // Initialize TGE
        tge.initialize(
            address(service),
            address(token),
            tokenId,
            uri,
            tgeInfo,
            protocolTokenFee
        );

        return (token, tge);
    }

    function _createInitialPreferenceTGE(
        address token,
        uint256 tokenId,
        string memory uri,
        ITGE.TGEInfo calldata tgeInfo,
        IToken.TokenInfo calldata tokenInfo,
        string memory metadataURI
    ) internal returns (address, ITGE) {
        // Create TGE
        ITGE tge = _createTGE(metadataURI, msg.sender);

        if (tokenId != 0) {
            // Create token contract
            token = address(
                service.tokenFactory().createTokenERC1155(
                    msg.sender,
                    tokenInfo,
                    address(tge)
                )
            );
        } else {
            // Create token contract
            token = address(
                service.tokenFactory().createToken(
                    msg.sender,
                    tokenInfo,
                    address(tge)
                )
            );
        }

        // Add token to Pool
        IPool(msg.sender).setToken(token, IToken.TokenType.Preference);

        // Initialize TGE
        tge.initialize(address(service), token, tokenId, uri, tgeInfo, 0);

        return (token, tge);
    }

    /**
     * @dev Create TGE contract
     * @param metadataURI TGE metadata URI
     * @param pool Pool address
     * @return tge TGE contract
     */
    function _createTGE(
        string memory metadataURI,
        address pool
    ) internal returns (ITGE tge) {
        // Create TGE contract
        tge = ITGE(address(new BeaconProxy(service.tgeBeacon(), "")));

        // Add TGE contract to registry
        service.registry().addContractRecord(
            address(tge),
            IRecordsRegistry.ContractType.TGE,
            metadataURI
        );

        // Add TGE event to registry
        service.registry().addEventRecord(
            pool,
            IRecordsRegistry.EventType.TGE,
            address(tge),
            0,
            ""
        );
    }
}

contracts/Token.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/ERC20CappedUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/ERC20VotesUpgradeable.sol";
import "./interfaces/IService.sol";
import "./interfaces/IToken.sol";
import "./interfaces/ITGE.sol";
import "./interfaces/IPool.sol";
import "./interfaces/registry/IRegistry.sol";
import "./libraries/ExceptionsLibrary.sol";

import "./interfaces/IPausable.sol";

/// @title Company (Pool) Token
/// @dev An expanded ERC20 contract, based on which tokens of various types are issued. At the moment, the protocol provides for 2 types of tokens: Governance, which must be created simultaneously with the pool, existing for the pool only in the singular and participating in voting, and Preference, which may be several for one pool and which do not participate in voting in any way.
contract Token is ERC20CappedUpgradeable, ERC20VotesUpgradeable, IToken {
    /// @dev Service address
    IService public service;

    /// @dev Pool address
    address public pool;

    /// @dev Token type
    TokenType public tokenType;

    /// @dev Preference token description, allows up to 5000 characters, for others - ""
    string public description;

    /// @dev List of all TGEs
    address[] public tgeList;

    /// @dev Token decimals
    uint8 private _decimals;

    /// @dev Total Vested tokens for all TGEs
    uint256 private totalVested;

    /// @dev List of all TGEs with locked tokens
    address[] private tgeWithLockedTokensList;

    /// @dev Total amount of tokens reserved for the minting protocol fee
    uint256 private totalProtocolFeeReserved;

    // INITIALIZER AND CONSTRUCTOR

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Token creation, can only be started once. At the same time, the TGE contract, which sells the created token, is necessarily simultaneously deployed and receives an entry in the Registry. For the Governance token, the Name field for the ERC20 standard is taken from the trademark of the Pool contract to which the deployed token belongs. For Preference tokens, you can set an arbitrary value of the Name field.
     * @param service_ Service
     * @param pool_ Pool
     * @param info Token info struct
     * @param primaryTGE_ Primary tge address
     */
    function initialize(
        IService service_,
        address pool_,
        TokenInfo memory info,
        address primaryTGE_
    ) external initializer {
        __ERC20Capped_init(info.cap);

        description = info.description;

        if (info.tokenType == TokenType.Preference) {
            __ERC20_init(info.name, info.symbol);
            _decimals = info.decimals;
        } else {
            __ERC20_init(info.name, info.symbol);
        }
        tgeList.push(primaryTGE_);
        tgeWithLockedTokensList.push(primaryTGE_);
        tokenType = info.tokenType;
        service = service_;
        pool = pool_;
    }

    // RESTRICTED FUNCTIONS

    /**
     * @dev Minting of new tokens. Only the TGE or Vesting contract can mint tokens, there is no other way to get an additional issue. If the user who is being minted does not have tokens, they are sent to delegation on his behalf.
     * @param to Recipient
     * @param amount Amount of tokens
     */
    function mint(address to, uint256 amount) external onlyTGEOrVesting {
        // Delegate to self if first mint and no delegatee set
        if (tokenType == IToken.TokenType.Governance) {
            if (balanceOf(to) == 0 && delegates(to) == address(0))
                _delegate(to, to);
        }

        // Mint tokens
        _mint(to, amount);
    }

    /**
     * @dev Burn token
     * @param from Target
     * @param amount Amount of tokens
     */
    function burn(address from, uint256 amount) external whenPoolNotPaused {
        // Check that sender is valid
        require(
            service.registry().typeOf(msg.sender) ==
                IRecordsRegistry.ContractType.TGE ||
                msg.sender == from,
            ExceptionsLibrary.INVALID_USER
        );

        // Burn tokens
        _burn(from, amount);
    }

    /**
     * @dev Add TGE to TGE archive list
     * @param tge TGE address
     */
    function addTGE(address tge) external onlyTGEFactory {
        tgeList.push(tge);
        tgeWithLockedTokensList.push(tge);
    }

    /**
     * @dev Set amount of tokens to  Total Vested tokens for all TGEs
     * @param amount amount of tokens
     */
    function setTGEVestedTokens(uint256 amount) external onlyTGEOrVesting {
        totalVested = amount;
    }

    /**
     * @dev Set amount of tokens to Total amount of tokens reserved for the minting protocol fee
     * @param amount amount of tokens
     */
    function setProtocolFeeReserved(uint256 amount) external onlyTGE {
        totalProtocolFeeReserved = amount;
    }

    // PUBLIC VIEW FUNCTIONS

    /**
     * @dev Return decimals
     * @return Decimals
     */
    function decimals()
        public
        view
        override(ERC20Upgradeable, IToken)
        returns (uint8)
    {
        if (tokenType == TokenType.Governance) {
            return 18;
        } else {
            return _decimals;
        }
    }

    /**
     * @dev Return cap
     * @return Cap
     */
    function cap()
        public
        view
        override(IToken, ERC20CappedUpgradeable)
        returns (uint256)
    {
        return super.cap();
    }

    /**
     * @dev Return cap
     * @return Cap
     */
    function symbol()
        public
        view
        override(IToken, ERC20Upgradeable)
        returns (string memory)
    {
        return super.symbol();
    }

    /**
     * @dev The given getter returns the total balance of the address that is not locked for transfer, taking into account all the TGEs with which this token was distributed. Is the difference.
     * @param account Account address
     * @return Unlocked balance of account
     */
    function unlockedBalanceOf(address account) public view returns (uint256) {
        // Get total account balance
        uint256 balance = balanceOf(account);

        // Iterate through TGE With Locked Tokens List to get locked balance
        address[] memory _tgeWithLockedTokensList = tgeWithLockedTokensList;
        uint256 totalLocked = 0;
        for (uint256 i; i < _tgeWithLockedTokensList.length; i++) {
            totalLocked += ITGE(_tgeWithLockedTokensList[i]).lockedBalanceOf(
                account
            );
        }

        // Return difference
        return balance - totalLocked;
    }

    /**
     * @dev Return if pool had a successful TGE
     * @return Is any TGE successful
     */
    function isPrimaryTGESuccessful() external view returns (bool) {
        return (ITGE(tgeList[0]).state() == ITGE.State.Successful);
    }

    /**
     * @dev Return list of pool's TGEs
     * @return TGE list
     */
    function getTGEList() external view returns (address[] memory) {
        return tgeList;
    }

    /**
     * @dev Return list of pool's TGEs with locked tokens
     * @return TGE list
     */

    function getTgeWithLockedTokensList()
        external
        view
        returns (address[] memory)
    {
        return tgeWithLockedTokensList;
    }

    /**
     * @dev Return list of pool's TGEs
     * @return TGE list
     */
    function lastTGE() external view returns (address) {
        return tgeList[tgeList.length - 1];
    }

    /**
     * @dev Getter returns the sum of all tokens that belong to a specific address, but are in vesting in TGE contracts associated with this token
     * @return Total vesting tokens
     */
    function getTotalTGEVestedTokens() public view returns (uint256) {
        return totalVested;
    }

    /**
     * @dev Getter returns the sum of all tokens reserved for the minting protocol fee
     * @return Total vesting tokens
     */
    function getTotalProtocolFeeReserved() public view returns (uint256) {
        return totalProtocolFeeReserved;
    }

    /**
     * @dev Getter returns the sum of all tokens that was minted or reserved for mint
     * @return Total vesting tokens
     */
    function totalSupplyWithReserves() public view returns (uint256) {
        uint256 _totalSupplyWithReserves = totalSupply() +
            getTotalTGEVestedTokens() +
            getTotalProtocolFeeReserved();

        return _totalSupplyWithReserves;
    }

    function isERC1155() public pure returns (bool) {
        return false;
    }

    // INTERNAL FUNCTIONS

    /**
     * @dev Transfer tokens from a given user.
     * Check to make sure that transfer amount is less or equal
     * to least amount of unlocked tokens for any proposal that user might have voted for.
     * @param from User address
     * @param to Recipient address
     * @param amount Amount of tokens
     */
    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override whenPoolNotPaused {
        // Update list of TGEs with locked tokens
        updateTgeWithLockedTokensList();

        // Check that locked tokens are not transferred
        require(
            amount <= unlockedBalanceOf(from),
            ExceptionsLibrary.LOW_UNLOCKED_BALANCE
        );

        if (tokenType == IToken.TokenType.Governance) {
            if (balanceOf(to) == 0 && delegates(to) == address(0))
                _delegate(to, to);
        }

        // Execute transfer
        super._transfer(from, to, amount);
    }

    /**
     * @dev Hook that is called after any transfer of tokens. This includes
     * minting and burning.
     */
    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override(ERC20Upgradeable, ERC20VotesUpgradeable) {
        super._afterTokenTransfer(from, to, amount);
    }

    // function delegate(
    //     address delegatee
    // ) public override(ERC20VotesUpgradeable, VotesUpgradeable) {
    //     service.registry().log(
    //         msg.sender,
    //         address(this),
    //         0,
    //         abi.encodeWithSelector(IToken.delegate.selector, delegatee)
    //     );

    //     super.delegate(delegatee);
    // }

    function transfer(
        address to,
        uint256 amount
    ) public override(ERC20Upgradeable, IToken) returns (bool) {
        service.registry().log(
            msg.sender,
            address(this),
            0,
            abi.encodeWithSelector(IToken.transfer.selector, to, amount)
        );

        return super.transfer(to, amount);
    }

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) public override(ERC20Upgradeable, IToken) returns (bool) {
        service.registry().log(
            msg.sender,
            address(this),
            0,
            abi.encodeWithSelector(
                IToken.transferFrom.selector,
                from,
                to,
                amount
            )
        );

        return super.transferFrom(from, to, amount);
    }

    /** @dev Creates `amount` tokens and assigns them to `account`, increasing
     * the total supply.
     */
    function _mint(
        address account,
        uint256 amount
    ) internal override(ERC20VotesUpgradeable, ERC20CappedUpgradeable) {
        super._mint(account, amount);
    }

    /**
     * @dev Destroys `amount` tokens from `account`, reducing the
     * total supply.
     */
    function _burn(
        address account,
        uint256 amount
    ) internal override(ERC20Upgradeable, ERC20VotesUpgradeable) {
        super._burn(account, amount);
    }

    // PRIVATE FUNCTIONS

    /**
     * @dev Updates tgeWithLockedTokensList. Removes TGE from list, if transfer is unlocked
     */
    function updateTgeWithLockedTokensList() private {
        address[] memory _tgeWithLockedTokensList = tgeWithLockedTokensList;
        for (uint256 i; i < _tgeWithLockedTokensList.length; i++) {
            // Check if transfer is unlocked
            if (ITGE(_tgeWithLockedTokensList[i]).transferUnlocked()) {
                // Remove tge from tgeWithLockedTokensList when transfer is unlocked
                tgeWithLockedTokensList[i] = tgeWithLockedTokensList[
                    tgeWithLockedTokensList.length - 1
                ];
                tgeWithLockedTokensList.pop();
            }
        }
    }

    // MODIFIERS

    modifier onlyPool() {
        require(msg.sender == pool, ExceptionsLibrary.NOT_POOL);
        _;
    }

    modifier onlyTGEFactory() {
        require(
            msg.sender == address(service.tgeFactory()),
            ExceptionsLibrary.NOT_TGE_FACTORY
        );
        _;
    }

    modifier onlyTGE() {
        require(
            service.registry().typeOf(msg.sender) ==
                IRecordsRegistry.ContractType.TGE,
            ExceptionsLibrary.NOT_TGE
        );
        _;
    }

    modifier onlyTGEOrVesting() {
        bool isTGE = service.registry().typeOf(msg.sender) ==
            IRecordsRegistry.ContractType.TGE;
        bool isVesting = address(service.vesting()) == msg.sender;
        require(isTGE || isVesting, ExceptionsLibrary.NOT_TGE);
        _;
    }

    modifier whenPoolNotPaused() {
        require(!IPausable(pool).paused(), ExceptionsLibrary.SERVICE_PAUSED);
        _;
    }
}

contracts/TokenERC1155.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/extensions/ERC1155SupplyUpgradeable.sol";
import "./interfaces/IService.sol";
import "./interfaces/ITokenERC1155.sol";
import "./interfaces/IToken.sol";
import "./interfaces/ITGE.sol";
import "./interfaces/IPool.sol";
import "./interfaces/registry/IRegistry.sol";
import "./libraries/ExceptionsLibrary.sol";

import "./interfaces/IPausable.sol";

/// @title Company (Pool) Token
/// @dev An expanded ERC20 contract, based on which tokens of various types are issued. At the moment, the protocol provides for 2 types of tokens: Governance, which must be created simultaneously with the pool, existing for the pool only in the singular and participating in voting, and Preference, which may be several for one pool and which do not participate in voting in any way.
contract TokenERC1155 is ERC1155SupplyUpgradeable, ITokenERC1155 {
    /// @dev Service address
    IService public service;

    string public symbol;

    mapping(uint256 => string) private _tokenURIs;

    mapping(uint256 => uint256) public cap;

    uint256 public lastTokenId;

    /// @dev Pool address
    address public pool;

    /// @dev Token type
    IToken.TokenType public tokenType;

    /// @dev Preference token name
    string public name;

    /// @dev Preference token description, allows up to 5000 characters, for others - ""
    string public description;

    /// @dev List of all TGEs
    mapping(uint256 => address[]) public tgeList;

    /// @dev Total Vested tokens for all TGEs
    mapping(uint256 => uint256) private totalVested;

    /// @dev List of all TGEs with locked tokens
    mapping(uint256 => address[]) private tgeWithLockedTokensList;

    /// @dev Total amount of tokens reserved for the minting protocol fee
    mapping(uint256 => uint256) private totalProtocolFeeReserved;

    // INITIALIZER AND CONSTRUCTOR

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Token creation, can only be started once. At the same time, the TGE contract, which sells the created token, is necessarily simultaneously deployed and receives an entry in the Registry. For the Governance token, the Name field for the ERC20 standard is taken from the trademark of the Pool contract to which the deployed token belongs. For Preference tokens, you can set an arbitrary value of the Name field.
     * @param _service Service
     * @param _pool Pool
     * @param _info Token info struct
     * @param _primaryTGE Primary tge address
     */
    function initialize(
        IService _service,
        address _pool,
        IToken.TokenInfo memory _info,
        address _primaryTGE
    ) external initializer {
        __ERC1155Supply_init();
        name = _info.name;
        symbol = _info.symbol;
        description = _info.description;
        lastTokenId = 1;
        tgeList[lastTokenId].push(_primaryTGE);
        tgeWithLockedTokensList[lastTokenId].push(_primaryTGE);
        tokenType = _info.tokenType;
        service = _service;
        pool = _pool;
    }

    // RESTRICTED FUNCTIONS

    /**
     * @dev Minting of new tokens. Only the TGE or Vesting contract can mint tokens, there is no other way to get an additional issue. If the user who is being minted does not have tokens, they are sent to delegation on his behalf.
     * @param to Recipient
     * @param tokenId tokenId
     * @param amount Amount of tokens
     */
    function mint(
        address to,
        uint256 tokenId,
        uint256 amount
    ) external override(ITokenERC1155) onlyTGEOrVesting {
        // Mint tokens
        _mint(to, tokenId, amount, "");
    }

    /**
     * @dev Burn token
     * @param from Target
     * @param tokenId tokenId
     * @param amount Amount of tokens
     */
    function burn(
        address from,
        uint256 tokenId,
        uint256 amount
    ) public override(ITokenERC1155) whenPoolNotPaused onlyTGEOrVesting {
        // Check that sender is valid
        require(
            service.registry().typeOf(msg.sender) ==
                IRecordsRegistry.ContractType.TGE ||
                msg.sender == from,
            ExceptionsLibrary.INVALID_USER
        );

        // Burn tokens
        _burn(from, tokenId, amount);
    }

    /**
     * @dev Add TGE to TGE archive list
     * @param tge TGE address
     */
    function addTGE(address tge, uint256 tokenId) external onlyTGEFactory {
        tgeList[tokenId].push(tge);
        tgeWithLockedTokensList[tokenId].push(tge);
    }

    /**
     * @dev Set amount of tokens to  Total Vested tokens for all TGEs
     * @param amount amount of tokens
     */
    function setTGEVestedTokens(
        uint256 amount,
        uint256 tokenId
    ) external onlyTGEOrVesting {
        totalVested[tokenId] = amount;
    }

    /**
     * @dev Set tokenid cap
     * @param _tokenId tokenId
     * @param _cap cap
     */
    function setTokenIdCap(
        uint256 _tokenId,
        uint256 _cap
    ) external onlyTGEFactory {
        cap[_tokenId] = _cap;
    }

    /**
     * @dev Set amount of tokens to Total amount of tokens reserved for the minting protocol fee
     * @param amount amount of tokens
     */
    function setProtocolFeeReserved(
        uint256 amount,
        uint256 tokenId
    ) external onlyTGE {
        totalProtocolFeeReserved[tokenId] = amount;
    }

    /**
     * @dev Set amount of tokens to Total amount of tokens reserved for the minting protocol fee
     * @param tokenId tokenId
     * @param tokenURI tokenURI
     */
    function setURI(uint256 tokenId, string memory tokenURI) external onlyTGE {
        _setURI(tokenId, tokenURI);
    }

    // // PUBLIC VIEW FUNCTIONS

    /**
     * @dev Return decimals
     * @return Decimals
     */
    function decimals() public pure returns (uint8) {
        return 0;
    }

    /**
     * @dev The given getter returns the total balance of the address that is not locked for transfer, taking into account all the TGEs with which this token was distributed. Is the difference.
     * @param account Account address
     * @param tokenId tokenId
     * @return Unlocked balance of account
     */
    function unlockedBalanceOf(
        address account,
        uint256 tokenId
    ) public view returns (uint256) {
        // Get total account balance
        uint256 balance = balanceOf(account, tokenId);

        // Iterate through TGE With Locked Tokens List to get locked balance
        address[] memory _tgeWithLockedTokensList = tgeWithLockedTokensList[
            tokenId
        ];
        uint256 totalLocked = 0;
        for (uint256 i; i < _tgeWithLockedTokensList.length; i++) {
            totalLocked += ITGE(_tgeWithLockedTokensList[i]).lockedBalanceOf(
                account
            );
        }

        // Return difference
        return balance - totalLocked;
    }

    /**
     * @dev Return if pool had a successful TGE
     * @return Is any TGE successful
     */
    function isPrimaryTGESuccessful(
        uint256 _tokenId
    ) external view returns (bool) {
        if (_tokenId > lastTokenId) return false;
        return (ITGE(tgeList[_tokenId][0]).state() == ITGE.State.Successful);
    }

    /**
     * @dev Return list of pool's TGEs
     * @return TGE list
     */
    function getTGEList(
        uint256 tokenId
    ) external view returns (address[] memory) {
        return tgeList[tokenId];
    }

    /**
     * @dev Return list of pool's TGEs with locked tokens
     * @return TGE list
     */

    function getTgeWithLockedTokensList(
        uint256 tokenId
    ) external view returns (address[] memory) {
        return tgeWithLockedTokensList[tokenId];
    }

    /**
     * @dev Return list of pool's TGEs
     * @return TGE list
     */
    function lastTGE(uint256 tokenId) external view returns (address) {
        return tgeList[tokenId][tgeList[tokenId].length - 1];
    }

    /**
     * @dev Getter returns the sum of all tokens that belong to a specific address, but are in vesting in TGE contracts associated with this token
     * @return Total vesting tokens
     */
    function getTotalTGEVestedTokens(
        uint256 tokenId
    ) public view returns (uint256) {
        return totalVested[tokenId];
    }

    /**
     * @dev Getter returns the sum of all tokens reserved for the minting protocol fee
     * @return Total vesting tokens
     */
    function getTotalProtocolFeeReserved(
        uint256 tokenId
    ) public view returns (uint256) {
        return totalProtocolFeeReserved[tokenId];
    }

    /**
     * @dev Getter returns the sum of all tokens that was minted or reserved for mint
     * @return Total vesting tokens
     */
    function totalSupplyWithReserves(
        uint256 tokenId
    ) public view returns (uint256) {
        uint256 _totalSupplyWithReserves = totalSupply(tokenId) +
            getTotalTGEVestedTokens(tokenId) +
            getTotalProtocolFeeReserved(tokenId);

        return _totalSupplyWithReserves;
    }

    function uri(uint256 tokenId) public view override returns (string memory) {
        return _tokenURIs[tokenId];
    }

    function isERC1155() external pure returns (bool) {
        return true;
    }

    function getURIList(
        uint256 limit,
        uint offset
    ) external view returns (string[] memory) {
        string[] memory result = new string[](limit);
        for (uint i = 0; i < limit && i <= lastTokenId; i++)
            result[i] = _tokenURIs[offset + i + 1];
        return result;
    }

    // INTERNAL FUNCTIONS

    /**
     * @dev Transfer tokens from a given user.
     * Check to make sure that transfer amount is less or equal
     * to least amount of unlocked tokens for any proposal that user might have voted for.
     * @param from User address
     * @param to Recipient address
     * @param amount Amount of tokens
     */
    function _transfer(
        address from,
        address to,
        uint256 tokenId,
        uint256 amount
    ) internal whenPoolNotPaused {
        // Execute transfer
        super._safeTransferFrom(from, to, tokenId, amount, "");
    }

    function transfer(
        address from,
        address to,
        uint256 tokenId,
        uint256 amount
    ) external override whenPoolNotPaused {
        // Execute transfer
        _transfer(from, to, tokenId, amount);
    }

    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal override {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
        if (from != address(0)) {
            for (uint256 i = 0; i < ids.length; ++i) {
                // Update list of TGEs with locked tokens
                _updateTgeWithLockedTokensList(ids[i]);

                // Check that locked tokens are not transferred
                require(
                    amounts[i] <= unlockedBalanceOf(from, ids[i]),
                    ExceptionsLibrary.LOW_UNLOCKED_BALANCE
                );
                service.registry().log(
                    msg.sender,
                    address(this),
                    0,
                    abi.encodeWithSelector(
                        ITokenERC1155.transfer.selector,
                        from,
                        to,
                        ids[i],
                        amounts[i]
                    )
                );
            }
        }
    }

    /**
     * @dev Sets `tokenURI` as the tokenURI of `tokenId`.
     */
    function _setURI(uint256 tokenId, string memory tokenURI) internal {
        _tokenURIs[tokenId] = tokenURI;
        emit URI(uri(tokenId), tokenId);
    }

    // PRIVATE FUNCTIONS

    /**
     * @dev Updates tgeWithLockedTokensList. Removes TGE from list, if transfer is unlocked
     */
    function _updateTgeWithLockedTokensList(uint256 tokenId) private {
        address[] memory _tgeWithLockedTokensList = tgeWithLockedTokensList[
            tokenId
        ];
        for (uint256 i; i < _tgeWithLockedTokensList.length; i++) {
            // Check if transfer is unlocked
            if (ITGE(_tgeWithLockedTokensList[i]).transferUnlocked()) {
                // Remove tge from tgeWithLockedTokensList when transfer is unlocked
                tgeWithLockedTokensList[tokenId][i] = tgeWithLockedTokensList[
                    tokenId
                ][tgeWithLockedTokensList[tokenId].length - 1];
                tgeWithLockedTokensList[tokenId].pop();
            }
        }
    }

    // MODIFIERS

    modifier onlyPool() {
        require(msg.sender == pool, ExceptionsLibrary.NOT_POOL);
        _;
    }

    modifier onlyTGEFactory() {
        require(
            msg.sender == address(service.tgeFactory()),
            ExceptionsLibrary.NOT_TGE_FACTORY
        );
        _;
    }

    modifier onlyTGE() {
        require(
            service.registry().typeOf(msg.sender) ==
                IRecordsRegistry.ContractType.TGE,
            ExceptionsLibrary.NOT_TGE
        );
        _;
    }

    modifier onlyTGEOrVesting() {
        bool isTGE = service.registry().typeOf(msg.sender) ==
            IRecordsRegistry.ContractType.TGE;
        bool isVesting = address(service.vesting()) == msg.sender;
        require(isTGE || isVesting, ExceptionsLibrary.NOT_TGE);
        _;
    }

    modifier whenPoolNotPaused() {
        require(!IPausable(pool).paused(), ExceptionsLibrary.SERVICE_PAUSED);
        _;
    }
}

contracts/TokenFactory.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/proxy/beacon/BeaconProxy.sol";
import "./interfaces/IService.sol";
import "./interfaces/ITokenFactory.sol";
import "./libraries/ExceptionsLibrary.sol";

contract TokenFactory is Initializable, ITokenFactory {
    // STORAGE

    /// @notice Service contract
    IService public service;

    // MODIFIERS

    modifier onlyTGEFactory() {
        require(
            msg.sender == address(service.tgeFactory()),
            ExceptionsLibrary.NOT_TGE_FACTORY
        );
        _;
    }

    // INITIALIZER

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Initializer function, can only be called once
     * @param service_ Service address
     */
    function initialize(IService service_) external initializer {
        service = service_;
    }

    /**
     * @dev Create token contract
     * @return token Token contract
     */
    function createToken(
        address pool,
        IToken.TokenInfo memory info,
        address primaryTGE
    ) external onlyTGEFactory returns (address token) {
        // Create token contract
        token = address(new BeaconProxy(service.tokenBeacon(), ""));

        // Initialize token
        IToken(token).initialize(service, pool, info, primaryTGE);

        // Add token contract to registry
        service.registry().addContractRecord(
            address(token),
            IToken(token).tokenType() == IToken.TokenType.Governance
                ? IRecordsRegistry.ContractType.GovernanceToken
                : IRecordsRegistry.ContractType.PreferenceToken,
            ""
        );
    }

    /**
     * @dev Create token contract ERC1155
     * @return token Token contract
     */
    function createTokenERC1155(
        address pool,
        IToken.TokenInfo memory info,
        address primaryTGE
    ) external onlyTGEFactory returns (address token) {
        // Create token contract
        token = address(new BeaconProxy(service.tokenERC1155Beacon(), ""));

        // Initialize token
        ITokenERC1155(token).initialize(service, pool, info, primaryTGE);

        // Add token contract to registry
        service.registry().addContractRecord(
            address(token),
            IToken(token).tokenType() == IToken.TokenType.Governance
                ? IRecordsRegistry.ContractType.GovernanceToken
                : IRecordsRegistry.ContractType.PreferenceToken,
            ""
        );
    }
}

contracts/Vesting.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "./libraries/ExceptionsLibrary.sol";
import "./interfaces/registry/IRegistry.sol";
import "./interfaces/ITGE.sol";
import "./interfaces/IVesting.sol";

contract Vesting is Initializable, IVesting {
    using SafeERC20Upgradeable for IToken;

    // CONSTANTS

    /// @notice Denominator for shares
    uint256 private constant DENOM = 100 * 10 ** 4;

    // STORAGE

    /// @notice Registry contract
    IRegistry public registry;

    /// @notice Mapping of TGE addresses to total amount of tokens vested there
    mapping(address => uint256) public totalVested;

    /// @notice Mapping (tge, account) to amount of tokens vested to that account in TGE
    mapping(address => mapping(address => uint256)) public vested;

    /// @notice Mapping (tge, account) to amount of already claimed vested tokens to that account in TGE
    mapping(address => mapping(address => uint256)) public claimed;

    /// @notice Mapping of TGE addresses to flags if claim TVL is reached for TGE
    /// @dev Users can claim their tokens only if claim TVL was reached.
    mapping(address => bool) public claimTVLReached;

    /// @notice Mapping (tge, account) to amount of resolved tokens in TGE
    mapping(address => mapping(address => uint256)) public resolved;

    // EVENTS

    /**
     * @dev Event emitted on token vesting.
     * @param tge TGE address
     * @param account Claimer address
     * @param amount Amount of vested tokens
     */
    event Vested(address tge, address account, uint256 amount);

    /**
     * @dev Event emitted on vested tokens recall
     * @param tge TGE address
     * @param account User address
     * @param amount Amount of recalled tokens
     */
    event Recall(address tge, address account, uint256 amount);

    /**
     * @dev Event emitted on token claim.
     * @param tge TGE address
     * @param account Claimer address
     * @param amount Amount of claimed tokens
     */
    event Claimed(address tge, address account, uint256 amount);

    /**
     * @dev Event emitted on vesting cancellation for account by resolver
     * @param tge TGE address
     * @param account Account address
     * @param amount Amount of burnt tokens
     */
    event Cancelled(address tge, address account, uint256 amount);

    // MODIFIERS

    modifier onlyTGE() {
        require(
            registry.typeOf(msg.sender) == IRecordsRegistry.ContractType.TGE,
            ExceptionsLibrary.NOT_TGE
        );
        _;
    }

    modifier onlyManager() {
        IService service = registry.service();
        require(
            service.hasRole(service.SERVICE_MANAGER_ROLE(), msg.sender),
            ExceptionsLibrary.NOT_WHITELISTED
        );
        _;
    }

    modifier onlyResolverOrTGE(address tge) {
        if (msg.sender != tge) {
            address[] memory resolvers = ITGE(tge)
                .getInfo()
                .vestingParams
                .resolvers;
            bool isResolver;
            for (uint256 i = 0; i < resolvers.length; i++) {
                if (resolvers[i] == msg.sender) {
                    isResolver = true;
                    break;
                }
            }
            require(isResolver, ExceptionsLibrary.NOT_RESOLVER);
        }
        _;
    }

    // INITIALIZER AND CONSTRUCTOR

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @notice Contract initializer
     * @param registry_ Protocol registry address
     */
    function initialize(IRegistry registry_) external initializer {
        registry = registry_;
    }

    // PUBLIC FUNCTIONS

    /**
     * @notice Vests token to given account (only called by TGE)
     * @param to Address to vest tokens to
     * @param amount Amount of tokens to vest
     */
    function vest(address to, uint256 amount) external onlyTGE {
        totalVested[msg.sender] += amount;
        vested[msg.sender][to] += amount;

        emit Vested(msg.sender, to, amount);
    }

    /**
     * @notice Set's claim TVL reached for TGE (only called by manager)
     * @param tge Adddress of the TGE
     */
    function setClaimTVLReached(address tge) external onlyManager {
        require(
            ITGE(tge).state() == ITGE.State.Successful,
            ExceptionsLibrary.WRONG_STATE
        );
        claimTVLReached[tge] = true;
    }

    /**
     * @notice Cancels vesting for given account in given tge, burning all remaining tokens (only called by TGE resolver or TGE)
     * @param tge TGE address
     * @param account Account address
     */
    function cancel(
        address tge,
        address account
    ) external onlyResolverOrTGE(tge) {
        uint256 amount = vestedBalanceOf(tge, account);

        vested[tge][account] -= amount;
        totalVested[tge] -= amount;

        resolved[tge][account] += amount;

        emit Cancelled(tge, account, amount);
    }

    /**
     * @notice Claims unlocked tokens from given TGE
     * @param tge TGE to claim from
     */
    function claim(address tge) external {
        uint256 amount = claimableBalanceOf(tge, msg.sender);
        require(amount > 0, ExceptionsLibrary.CLAIM_NOT_AVAILABLE);

        claimed[tge][msg.sender] += amount;
        totalVested[tge] -= amount;

        address token = ITGE(tge).token();
        uint256 tokenId = ITGE(tge).tokenId();
        if (ITGE(tge).isERC1155TGE()) {
            ITokenERC1155(token).setTGEVestedTokens(
                ITokenERC1155(token).getTotalTGEVestedTokens(tokenId) - amount,
                tokenId
            );

            ITokenERC1155(token).mint(msg.sender, tokenId, amount);
        } else {
            IToken(token).setTGEVestedTokens(
                IToken(token).getTotalTGEVestedTokens() - amount
            );

            IToken(token).mint(msg.sender, amount);
        }
        emit Claimed(tge, msg.sender, amount);
    }

    // PUBLIC VIEW FUNCTIONS

    /**
     * @notice Gets vesting params for TGE
     * @param tge TGE address
     */
    function vestingParams(
        address tge
    ) public view returns (VestingParams memory) {
        return ITGE(tge).getInfo().vestingParams;
    }

    /**
     * @notice Validates vesting params for TGE
     * @param params Vesting params
     * @return True if params are valid (reverts otherwise)
     */
    function validateParams(
        VestingParams memory params
    ) public pure returns (bool) {
        require(
            params.cliffShare + params.spans * params.spanShare <= DENOM,
            ExceptionsLibrary.SHARES_SUM_EXCEEDS_ONE
        );
        return true;
    }

    /**
     * @notice Gets total unlocked balance of account in TGE (including already claimed tokens)
     * @param tge TGE address
     * @param account Account address
     */
    function unlockedBalanceOf(
        address tge,
        address account
    ) public view returns (uint256) {
        // In active or failed TGE nothing is unlocked
        if (ITGE(tge).state() != ITGE.State.Successful) {
            return 0;
        }

        // Is claim TVL is non-zero and is not reached, nothing is unlocked
        VestingParams memory params = vestingParams(tge);
        if (params.claimTVL > 0 && !claimTVLReached[tge]) {
            return 0;
        }

        // Determine unlocked amount
        uint256 tgeEnd = ITGE(tge).getEnd();
        if (block.number < tgeEnd + params.cliff) {
            // If cliff is not exceeded, nothing is unlocked yet
            return 0;
        } else if (
            block.number <
            tgeEnd + params.cliff + params.spans * params.spanDuration
        ) {
            // If cliff is reached, but not all the period passed, calculate vested amount
            uint256 spansUnlocked = (block.number - tgeEnd - params.cliff) /
                params.spanDuration;
            uint256 totalShare = params.cliffShare +
                spansUnlocked *
                params.spanShare;
            return (vested[tge][account] * totalShare) / DENOM;
        } else {
            // Otherwise everything is unlocked
            return vested[tge][account];
        }
    }

    /**
     * @notice Gets claimable balance of account in TGE
     * @param tge TGE address
     * @param account Account address
     */
    function claimableBalanceOf(
        address tge,
        address account
    ) public view returns (uint256) {
        return unlockedBalanceOf(tge, account) - claimed[tge][account];
    }

    /**
     * @notice Gets remaining vested balance of account in TGE (all unclaimed tokens, even locked)
     * @param tge TGE address
     * @param account Account address
     */
    function vestedBalanceOf(
        address tge,
        address account
    ) public view returns (uint256) {
        return vested[tge][account] - claimed[tge][account];
    }
}
